import React, { useState } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  Box,
  Button,
  Chip,
  Paper,
  IconButton,
  Tooltip,
} from '@mui/material';
import {
  Security,
  CloudUpload,
  Scanner,
  Shield,
  Analytics,
  Speed,
  AutoFixHigh,
  Assessment,
  Visibility,
  Terminal,
  Settings,
  ContentCopy,
  GitHub,
  Storage,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

const Dashboard: React.FC = () => {
  const navigate = useNavigate();
  const [copiedCommand, setCopiedCommand] = useState('');

  const copyToClipboard = (text: string, command: string) => {
    navigator.clipboard.writeText(text);
    setCopiedCommand(command);
    setTimeout(() => setCopiedCommand(''), 2000);
  };

  const features = [
    {
      title: 'Smart Log Detective',
      description: 'Our AI buddy finds weird stuff in your AWS logs - like a digital Sherlock Holmes!',
      icon: <Analytics sx={{ fontSize: 48, color: '#ff4d4d' }} />,
      action: () => navigate('/logs'),
      actionText: 'Start Detecting!',
      highlights: ['5 Log Types', 'Real-time Magic', 'AI Detective'],
      rotation: '-2deg',
      bgColor: '#fff9c4' // Post-it yellow
    },
    {
      title: 'Code Security Scanner',
      description: 'Scans your Terraform like a security guard with 150+ rules - no bad code gets past!',
      icon: <Scanner sx={{ fontSize: 48, color: '#2d5da1' }} />,
      action: () => navigate('/scanner'),
      actionText: 'Scan My Code!',
      highlights: ['150+ Rules', 'Terraform Pro', 'Instant Results'],
      rotation: '1.5deg',
      bgColor: '#ffffff'
    },
    {
      title: 'Rule Generator Wizard',
      description: 'Creates security rules automatically and learns from you - like having a smart assistant!',
      icon: <AutoFixHigh sx={{ fontSize: 48, color: '#ff4d4d' }} />,
      action: () => navigate('/rules'),
      actionText: 'Manage Rules!',
      highlights: ['Auto-Magic Rules', 'Learns from You', 'Smart Assistant'],
      rotation: '-1deg',
      bgColor: '#ffffff'
    }
  ];

  const stats = [
    { label: 'AWS Services', value: '35+', icon: <Shield />, rotation: '2deg' },
    { label: 'Security Rules', value: '150+', icon: <Assessment />, rotation: '-1deg' },
    { label: 'Log Types', value: '5', icon: <Visibility />, rotation: '1.5deg' },
    { label: 'Speed', value: '<50ms', icon: <Speed />, rotation: '-2deg' }
  ];

  const installationMethods = [
    {
      title: 'Quick CLI Setup',
      icon: <Terminal sx={{ fontSize: 32, color: '#ff4d4d' }} />,
      commands: [
        { label: 'Install it!', command: 'pip install securon-cli', description: 'Get the CLI tool on your machine' },
        { label: 'Scan stuff!', command: 'securon scan file main.tf', description: 'Check your Terraform files' },
        { label: 'Find bugs!', command: 'securon logs analyze vpc-flow.json', description: 'Look for weird things in logs' }
      ],
      rotation: '1deg'
    },
    {
      title: 'Docker Magic',
      icon: <Storage sx={{ fontSize: 32, color: '#2d5da1' }} />,
      commands: [
        { label: 'Get the image', command: 'docker pull securon/platform:latest', description: 'Download our container' },
        { label: 'Run the scan', command: 'docker run -v $(pwd):/workspace securon/platform scan', description: 'Scan in a container' }
      ],
      rotation: '-1.5deg'
    },
    {
      title: 'CI/CD Automation',
      icon: <Settings sx={{ fontSize: 32, color: '#ff4d4d' }} />,
      commands: [
        { label: 'GitHub Setup', command: 'uses: securon/action@v1\nwith:\n  terraform-path: ./infrastructure', description: 'Add to your GitHub workflow' },
        { label: 'GitLab Setup', command: 'securon-scan:\n  script:\n    - securon scan file *.tf', description: 'Add to .gitlab-ci.yml' }
      ],
      rotation: '0.5deg'
    }
  ];

  return (
    <Box sx={{ minHeight: '100vh', position: 'relative', py: 4 }}>
      <Container maxWidth="lg">
        {/* Hand-Drawn Hero Section */}
        <Box sx={{ 
          textAlign: 'center', 
          py: 8,
          position: 'relative',
          mb: 8,
        }}>
          {/* Decorative Doodles */}
          <Box
            sx={{
              position: 'absolute',
              top: 20,
              left: 20,
              fontSize: '2rem',
              transform: 'rotate(-15deg)',
              opacity: 0.6,
            }}
          >
            üîí
          </Box>
          <Box
            sx={{
              position: 'absolute',
              top: 30,
              right: 30,
              fontSize: '1.5rem',
              transform: 'rotate(20deg)',
              opacity: 0.4,
            }}
          >
            ‚ö°
          </Box>

          {/* Wobbly Logo */}
          <Box
            sx={{
              width: 120,
              height: 120,
              backgroundColor: '#ff4d4d',
              borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
              border: '4px solid #2d2d2d',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              mx: 'auto',
              mb: 4,
              transform: 'rotate(-3deg)',
              boxShadow: '6px 6px 0px 0px #2d2d2d',
              position: 'relative',
              '&::before': {
                content: '"‚ú®"',
                position: 'absolute',
                top: -15,
                right: -15,
                fontSize: '2rem',
                transform: 'rotate(25deg)',
              }
            }}
          >
            <Security 
              sx={{ 
                fontSize: 48, 
                color: '#ffffff',
              }} 
            />
          </Box>

          <Typography 
            variant="h1" 
            component="h1" 
            gutterBottom 
            sx={{ 
              fontSize: { xs: '3rem', md: '4rem' },
              color: '#2d2d2d',
              mb: 2,
              transform: 'rotate(-0.5deg)',
              position: 'relative',
              '&::after': {
                content: '"!"',
                color: '#ff4d4d',
                transform: 'rotate(15deg)',
                display: 'inline-block',
                marginLeft: '0.2em',
              }
            }}
          >
            Securon Platform
          </Typography>

          <Typography 
            variant="h4" 
            sx={{ 
              mb: 4, 
              color: '#2d2d2d',
              fontFamily: '"Patrick Hand", cursive',
              fontSize: '1.5rem',
              transform: 'rotate(0.5deg)',
              position: 'relative',
              '&::before': {
                content: '""',
                position: 'absolute',
                bottom: -5,
                left: '20%',
                right: '20%',
                height: 3,
                backgroundColor: '#fff9c4', // Yellow highlight
                zIndex: -1,
                transform: 'rotate(-1deg)',
              }
            }}
          >
            Human-Friendly Cloud Security
          </Typography>

          <Typography 
            variant="body1" 
            sx={{ 
              mb: 6, 
              maxWidth: 600, 
              mx: 'auto', 
              color: '#2d2d2d',
              lineHeight: 1.8,
              fontSize: '1.2rem',
            }}
          >
            We make cloud security simple and fun! Our AI finds problems, 
            scans your code, and helps keep your stuff safe. No boring corporate nonsense here! üéâ
          </Typography>

          <Box sx={{ display: 'flex', gap: 3, justifyContent: 'center', flexWrap: 'wrap' }}>
            <Button
              variant="contained"
              size="large"
              onClick={() => navigate('/logs')}
              sx={{ 
                px: 4, 
                py: 2,
                fontSize: '1.2rem',
                transform: 'rotate(-1deg)',
                '&:hover': {
                  transform: 'rotate(1deg) scale(1.05)',
                }
              }}
            >
              Let's Start! üöÄ
            </Button>
            <Button
              variant="outlined"
              size="large"
              onClick={() => document.getElementById('cli-section')?.scrollIntoView({ behavior: 'smooth' })}
              sx={{ 
                px: 4, 
                py: 2,
                fontSize: '1.2rem',
                transform: 'rotate(1deg)',
                '&:hover': {
                  transform: 'rotate(-1deg) scale(1.05)',
                }
              }}
            >
              Get CLI Tools üõ†Ô∏è
            </Button>
          </Box>

          {/* Hand-drawn arrow pointing to buttons */}
          <Box
            sx={{
              position: 'absolute',
              bottom: -20,
              left: '60%',
              fontSize: '2rem',
              transform: 'rotate(45deg)',
              opacity: 0.7,
              display: { xs: 'none', md: 'block' }
            }}
          >
            ‚ÜóÔ∏è
          </Box>
        </Box>

        {/* Hand-Drawn Stats Section */}
        <Box sx={{ mb: 10 }}>
          <Typography 
            variant="h3" 
            sx={{ 
              textAlign: 'center',
              mb: 6,
              color: '#2d2d2d',
              transform: 'rotate(-0.5deg)',
              position: 'relative',
              '&::before': {
                content: '"üìä"',
                position: 'absolute',
                left: -50,
                top: 0,
                transform: 'rotate(-20deg)',
              }
            }}
          >
            Cool Numbers!
          </Typography>

          <Grid container spacing={4}>
            {stats.map((stat, index) => (
              <Grid item xs={6} md={3} key={index}>
                <Card 
                  sx={{ 
                    textAlign: 'center', 
                    p: 3,
                    height: '100%',
                    transform: `rotate(${stat.rotation})`,
                    backgroundColor: index % 2 === 0 ? '#fff9c4' : '#ffffff',
                    position: 'relative',
                    '&:hover': {
                      transform: `rotate(${stat.rotation === '2deg' ? '-2deg' : '2deg'}) scale(1.05)`,
                    },
                    '&::before': {
                      content: `"${index + 1}"`,
                      position: 'absolute',
                      top: -10,
                      right: -10,
                      width: 25,
                      height: 25,
                      backgroundColor: '#ff4d4d',
                      color: '#ffffff',
                      borderRadius: '50%',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '0.9rem',
                      fontFamily: '"Kalam", cursive',
                      fontWeight: 700,
                      border: '2px solid #2d2d2d',
                      transform: 'rotate(15deg)',
                    }
                  }}
                >
                  {/* Wobbly Icon Container */}
                  <Box
                    sx={{
                      width: 60,
                      height: 60,
                      backgroundColor: '#ff4d4d',
                      borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
                      border: '3px solid #2d2d2d',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      mx: 'auto',
                      mb: 2,
                      boxShadow: '3px 3px 0px 0px #2d2d2d',
                    }}
                  >
                    {React.cloneElement(stat.icon, {
                      sx: { 
                        fontSize: 28, 
                        color: '#ffffff',
                      }
                    })}
                  </Box>

                  <Typography 
                    variant="h4" 
                    sx={{ 
                      color: '#2d2d2d', 
                      fontFamily: '"Kalam", cursive',
                      mb: 1,
                      fontWeight: 700,
                    }}
                  >
                    {stat.value}
                  </Typography>
                  <Typography 
                    variant="body1" 
                    sx={{ 
                      color: '#2d2d2d',
                      fontFamily: '"Patrick Hand", cursive',
                    }}
                  >
                    {stat.label}
                  </Typography>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>

        {/* Hand-Drawn CLI Installation Section */}
        <Box id="cli-section" sx={{ mb: 10 }}>
          <Typography 
            variant="h3" 
            sx={{ 
              textAlign: 'center',
              mb: 6,
              color: '#2d2d2d',
              transform: 'rotate(0.5deg)',
              position: 'relative',
              '&::before': {
                content: '"‚öôÔ∏è"',
                position: 'absolute',
                left: -50,
                top: 0,
                transform: 'rotate(-15deg)',
              }
            }}
          >
            Get Started Easy!
          </Typography>

          <Grid container spacing={4}>
            {installationMethods.map((method, index) => (
              <Grid item xs={12} md={4} key={index}>
                <Card 
                  sx={{ 
                    height: '100%',
                    transform: `rotate(${method.rotation})`,
                    position: 'relative',
                    '&:hover': {
                      transform: `rotate(${method.rotation === '1deg' ? '-1deg' : '1deg'}) scale(1.02)`,
                    },
                    // Tape decoration
                    '&::before': {
                      content: '""',
                      position: 'absolute',
                      top: 15,
                      left: '50%',
                      transform: 'translateX(-50%) rotate(-5deg)',
                      width: 60,
                      height: 20,
                      backgroundColor: 'rgba(255, 255, 196, 0.8)',
                      border: '1px solid rgba(45, 45, 45, 0.2)',
                      zIndex: 1,
                    }
                  }}
                >
                  <CardContent sx={{ p: 4 }}>
                    {/* Icon Header */}
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 3, gap: 2 }}>
                      <Box
                        sx={{
                          width: 50,
                          height: 50,
                          backgroundColor: '#ff4d4d',
                          borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
                          border: '3px solid #2d2d2d',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          boxShadow: '2px 2px 0px 0px #2d2d2d',
                        }}
                      >
                        {React.cloneElement(method.icon, {
                          sx: { 
                            fontSize: 24, 
                            color: '#ffffff',
                          }
                        })}
                      </Box>
                      <Typography 
                        variant="h5" 
                        sx={{ 
                          color: '#2d2d2d',
                          fontFamily: '"Kalam", cursive',
                          fontWeight: 700,
                        }}
                      >
                        {method.title}
                      </Typography>
                    </Box>

                    <Box>
                      {method.commands.map((cmd, idx) => (
                        <Box key={idx} sx={{ mb: 3 }}>
                          <Typography 
                            variant="subtitle1" 
                            sx={{ 
                              color: '#ff4d4d', 
                              fontWeight: 600, 
                              mb: 1,
                              fontFamily: '"Kalam", cursive',
                            }}
                          >
                            {cmd.label}
                          </Typography>
                          <Paper 
                            sx={{ 
                              p: 2, 
                              backgroundColor: '#2d2d2d',
                              borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
                              border: '2px solid #2d2d2d',
                              position: 'relative',
                              '&:hover': { 
                                boxShadow: '2px 2px 0px 0px #ff4d4d',
                              },
                              transition: 'all 0.1s ease'
                            }}
                          >
