import React, { useState, useCallback } from 'react';
import {
  Box,
  Button,
  Typography,
  LinearProgress,
  Alert,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemText,
  Chip,
  Grid
} from '@mui/material';
import { CloudUpload, CheckCircle, Error } from '@mui/icons-material';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { AnomalyResult } from '../types';

interface UploadResult {
  message: string;
  logs_processed: number;
  anomalies_detected: number;
  candidate_rules_generated: number;
  anomalies: AnomalyResult[];
}

const LogUpload: React.FC = () => {
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadResult, setUploadResult] = useState<UploadResult | null>(null);
  const [error, setError] = useState<string | null>(null);

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    if (acceptedFiles.length === 0) return;

    setUploading(true);
    setUploadProgress(0);
    setError(null);
    setUploadResult(null);

    try {
      const formData = new FormData();
      acceptedFiles.forEach(file => {
        formData.append('files', file);
      });

      const response = await axios.post('/api/logs/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        onUploadProgress: (progressEvent) => {
          if (progressEvent.total) {
            const progress = Math.round((progressEvent.loaded * 100) / progressEvent.total);
            setUploadProgress(progress);
          }
        },
      });

      setUploadResult(response.data);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to upload logs');
    } finally {
      setUploading(false);
      setUploadProgress(0);
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/json': ['.json'],
      'text/plain': ['.log', '.txt']
    },
    multiple: true
  });

  const getSeverityColor = (severity: number) => {
    if (severity >= 0.8) return 'error';
    if (severity >= 0.6) return 'warning';
    if (severity >= 0.4) return 'info';
    return 'success';
  };

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Log Upload & Analysis
      </Typography>

      <Card sx={{ mb: 3 }}>
        <CardContent>
          <Box
            {...getRootProps()}
            sx={{
              border: '2px dashed',
              borderColor: isDragActive ? 'primary.main' : 'grey.300',
              borderRadius: 2,
              p: 4,
              textAlign: 'center',
              cursor: 'pointer',
              bgcolor: isDragActive ? 'action.hover' : 'background.paper',
              '&:hover': {
                bgcolor: 'action.hover',
              },
            }}
          >
            <input {...getInputProps()} />
            <CloudUpload sx={{ fontSize: 48, color: 'grey.500', mb: 2 }} />
            <Typography variant="h6" gutterBottom>
              {isDragActive ? 'Drop the files here' : 'Drag & drop log files here'}
            </Typography>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              or click to select files
            </Typography>
            <Typography variant="caption" color="text.secondary">
              Supported formats: JSON, LOG, TXT (VPC Flow Logs, CloudTrail, IAM logs)
            </Typography>
          </Box>
        </CardContent>
      </Card>

      {uploading && (
        <Box sx={{ mb: 3 }}>
          <Typography variant="body2" gutterBottom>
            Uploading and processing logs...
          </Typography>
          <LinearProgress variant="determinate" value={uploadProgress} />
        </Box>
      )}

      {error && (
        <Alert severity="error" sx={{ mb: 3 }} icon={<Error />}>
          {error}
        </Alert>
      )}

      {uploadResult && (
        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Alert severity="success" sx={{ mb: 2 }} icon={<CheckCircle />}>
              {uploadResult.message}
            </Alert>

            <Grid container spacing={2} sx={{ mb: 2 }}>
              <Grid item xs={12} sm={3}>
                <Typography variant="h6" color="primary">
                  {uploadResult.logs_processed}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Logs Processed
                </Typography>
              </Grid>
              <Grid item xs={12} sm={3}>
                <Typography variant="h6" color="warning.main">
                  {uploadResult.anomalies_detected}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Anomalies Detected
                </Typography>
              </Grid>
              <Grid item xs={12} sm={3}>
                <Typography variant="h6" color="info.main">
                  {uploadResult.candidate_rules_generated}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Candidate Rules
                </Typography>
              </Grid>
            </Grid>

            {uploadResult.anomalies.length > 0 && (
              <Box>
                <Typography variant="h6" gutterBottom>
                  Detected Anomalies
                </Typography>
                <List>
                  {uploadResult.anomalies.map((anomaly, index) => (
                    <ListItem key={index} divider>
                      <ListItemText
                        primary={
                          <Box display="flex" alignItems="center" gap={1}>
                            <Typography variant="subtitle1">
                              {anomaly.type.replace('_', ' ')}
                            </Typography>
                            <Chip
                              label={`${Math.round(anomaly.severity * 100)}%`}
                              color={getSeverityColor(anomaly.severity)}
                              size="small"
                            />
                            <Chip
                              label={`${Math.round(anomaly.confidence * 100)}% confidence`}
                              variant="outlined"
                              size="small"
                            />
                          </Box>
                        }
                        secondary={
                          <Box>
                            <Typography variant="body2" color="text.secondary">
                              Affected Resources: {anomaly.affected_resources.join(', ')}
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                              Time Window: {anomaly.time_window.start} - {anomaly.time_window.end}
                            </Typography>
                          </Box>
                        }
                      />
                    </ListItem>
                  ))}
                </List>
              </Box>
            )}
          </CardContent>
        </Card>
      )}
    </Box>
  );
};

export default LogUpload;
