import React, { useState, useCallback, useEffect } from 'react';
import {
  Box,
  Typography,
  LinearProgress,
  Alert,
  Card,
  CardContent,
  Chip,
  Grid,
  Paper,
  Stepper,
  Step,
  StepLabel,
  StepContent,
  Fade,
  Zoom,
  Slide,
  CircularProgress,
  Avatar,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Divider,
  IconButton,
  Collapse,
  Badge,
  Tooltip
} from '@mui/material';
import { 
  CloudUpload, 
  CheckCircle, 
  Error, 
  BugReport,
  Psychology,
  Analytics,
  Security,
  Timeline,
  Visibility,
  ExpandMore,
  ExpandLess,
  TrendingUp,
  Warning,
  Shield,
  Speed,
  AutoFixHigh,
  SmartToy,
  DataUsage,
  NetworkCheck,
  Assessment
} from '@mui/icons-material';
import { useDropzone } from 'react-dropzone';
import apiClient from '../config/api';
// Types defined inline

interface UploadResult {
  message: string;
  logs_processed: number;
  anomalies_detected: number;
  candidate_rules_generated: number;
  anomalies: any[];
}

interface MLProcessingStep {
  id: string;
  label: string;
  description: string;
  icon: React.ReactNode;
  completed: boolean;
  active: boolean;
  duration?: number;
}

const LogUpload: React.FC = () => {
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadResult, setUploadResult] = useState<UploadResult | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [processingSteps, setProcessingSteps] = useState<MLProcessingStep[]>([]);
  const [activeStep, setActiveStep] = useState(0);
  const [showMLInsights, setShowMLInsights] = useState(false);
  const [expandedAnomalies, setExpandedAnomalies] = useState<Set<number>>(new Set());
  const [animationTrigger, setAnimationTrigger] = useState(0);

  const initializeMLSteps = useCallback(() => {
    const steps: MLProcessingStep[] = [
      {
        id: 'upload',
        label: 'Data Ingestion',
        description: 'Uploading and parsing log files',
        icon: <CloudUpload />,
        completed: false,
        active: false
      },
      {
        id: 'preprocessing',
        label: 'Data Preprocessing',
        description: 'Normalizing and structuring log data',
        icon: <DataUsage />,
        completed: false,
        active: false,
        duration: 2000
      },
      {
        id: 'feature_extraction',
        label: 'Feature Extraction',
        description: 'Extracting behavioral patterns and features',
        icon: <Analytics />,
        completed: false,
        active: false,
        duration: 3000
      },
      {
        id: 'ml_analysis',
        label: 'ML Analysis',
        description: 'Running anomaly detection algorithms',
        icon: <SmartToy />,
        completed: false,
        active: false,
        duration: 4000
      },
      {
        id: 'pattern_recognition',
        label: 'Pattern Recognition',
        description: 'Identifying security threats and anomalies',
        icon: <Psychology />,
        completed: false,
        active: false,
        duration: 2500
      },
      {
        id: 'rule_generation',
        label: 'Rule Generation',
        description: 'Generating security rules from findings',
        icon: <AutoFixHigh />,
        completed: false,
        active: false,
        duration: 1500
      }
    ];
    setProcessingSteps(steps);
    return steps;
  }, []);

  const simulateMLProcessing = useCallback(async (steps: MLProcessingStep[]) => {
    for (let i = 0; i < steps.length; i++) {
      setActiveStep(i);

      // Update step to active
      setProcessingSteps(prev => prev.map((step, idx) => ({
        ...step,
        active: idx === i,
        completed: idx < i
      })));

      // Wait for step duration
      if (steps[i].duration) {
        await new Promise(resolve => setTimeout(resolve, steps[i].duration));
      }

      // Complete the step
      setProcessingSteps(prev => prev.map((step, idx) => ({
        ...step,
        active: false,
        completed: idx <= i
      })));
    }
  }, []);

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    if (acceptedFiles.length === 0) return;

    console.log('üìÅ Files dropped:', acceptedFiles.map(f => f.name));

    setUploading(true);
    setUploadProgress(0);
    setError(null);
    setUploadResult(null);
    setShowMLInsights(false);
    setAnimationTrigger(prev => prev + 1);

    // Initialize ML processing steps
    const steps = initializeMLSteps();

    try {
      const formData = new FormData();
      acceptedFiles.forEach(file => {
        formData.append('files', file);
        console.log(`üìé Added file: ${file.name} (${file.size} bytes)`);
      });

      // Start ML processing simulation
      const processingPromise = simulateMLProcessing(steps);

      console.log('üöÄ Sending request to backend...');
      const response = await apiClient.post('/api/logs/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        onUploadProgress: (progressEvent) => {
          if (progressEvent.total) {
            const progress = Math.round((progressEvent.loaded * 100) / progressEvent.total);
            setUploadProgress(progress);
            console.log(`üìä Upload progress: ${progress}%`);
          }
        },
      });

      // Wait for both upload and ML processing simulation
      await processingPromise;

      console.log('‚úÖ Upload successful:', response.data);
      setUploadResult(response.data);

      // Show ML insights with delay for dramatic effect
      setTimeout(() => {
        setShowMLInsights(true);
      }, 1000);

    } catch (err: any) {
      console.error('‚ùå Upload failed:', err);
      setError(err.response?.data?.detail || 'Failed to upload logs');
    } finally {
      setUploading(false);
      setUploadProgress(0);
    }
  }, [initializeMLSteps, simulateMLProcessing]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    multiple: true,
    // Accept all file types - backend will validate
    noClick: false,
    noKeyboard: false
  });

  const getSeverityColor = (severity: number) => {
    if (severity >= 0.8) return 'error';
    if (severity >= 0.6) return 'warning';
    if (severity >= 0.4) return 'info';
    return 'success';
  };

  const toggleAnomalyExpansion = (index: number) => {
    const newExpanded = new Set(expandedAnomalies);
    if (newExpanded.has(index)) {
      newExpanded.delete(index);
    } else {
      newExpanded.add(index);
    }
    setExpandedAnomalies(newExpanded);
  };

  const getMLInsightIcon = (type: string) => {
    switch (type) {
      case 'PORT_SCAN': return <NetworkCheck />;
      case 'SUSPICIOUS_IP': return <Warning />;
      case 'BRUTE_FORCE': return <Security />;
      case 'UNUSUAL_API': return <Assessment />;
      default: return <BugReport />;
    }
  };

  const getAnomalyTypeDescription = (type: string) => {
    const descriptions = {
      'PORT_SCAN': 'Systematic scanning of network ports indicating reconnaissance activity',
      'SUSPICIOUS_IP': 'IP address exhibiting unusual or potentially malicious behavior patterns',
      'BRUTE_FORCE': 'Repeated authentication attempts suggesting credential attacks',
      'UNUSUAL_API': 'Abnormal API usage patterns that deviate from baseline behavior'
    };
    return descriptions[type as keyof typeof descriptions] || 'Anomalous behavior detected by ML algorithms';
  };

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Log Upload & Analysis
      </Typography>

      <Card sx={{ mb: 3 }}>
        <CardContent>
          <Box
            {...getRootProps()}
            sx={{
              border: '2px dashed',
              borderColor: isDragActive ? 'primary.main' : 'grey.300',
              borderRadius: 2,
              p: 4,
              textAlign: 'center',
              cursor: 'pointer',
              bgcolor: isDragActive ? 'action.hover' : 'background.paper',
              '&:hover': {
                bgcolor: 'action.hover',
              },
            }}
          >
            <input {...getInputProps()} />
            <CloudUpload sx={{ fontSize: 48, color: 'grey.500', mb: 2 }} />
            <Typography variant="h6" gutterBottom>
              {isDragActive ? 'Drop the files here' : 'Drag & drop log files here'}
            </Typography>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              or click to select files
            </Typography>
            <Typography variant="caption" color="text.secondary">
              Supported formats: .json, .log, .txt files (All AWS log types: VPC Flow, CloudTrail, IAM, WAF, ALB)
            </Typography>
          </Box>
        </CardContent>
      </Card>

      {uploading && (
        <Box sx={{ mb: 3 }}>
          <Typography variant="body2" gutterBottom>
            Uploading and processing logs...
          </Typography>
          <LinearProgress variant="determinate" value={uploadProgress} />
        </Box>
      )}

      {error && (
        <Alert severity="error" sx={{ mb: 3 }} icon={<Error />}>
          {error}
        </Alert>
      )}

      {uploadResult && (
        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Alert severity="success" sx={{ mb: 2 }} icon={<CheckCircle />}>
              {uploadResult.message}
            </Alert>

            <Grid container spacing={2} sx={{ mb: 2 }}>
              <Grid item xs={12} sm={3}>
                <Typography variant="h6" color="primary">
                  {uploadResult.logs_processed}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Logs Processed
                </Typography>
              </Grid>
              <Grid item xs={12} sm={3}>
                <Typography variant="h6" color="warning.main">
                  {uploadResult.anomalies_detected}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Anomalies Detected
                </Typography>
              </Grid>
              <Grid item xs={12} sm={3}>
                <Typography variant="h6" color="info.main">
                  {uploadResult.candidate_rules_generated}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Candidate Rules
                </Typography>
              </Grid>
            </Grid>

            {uploadResult.anomalies.length > 0 && (
              <Box>
                <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <BugReport color="warning" />
                  Security Analysis Results
                </Typography>

                {uploadResult.anomalies.map((anomaly, index) => (
                  <Card key={index} sx={{ mb: 2, border: '1px solid', borderColor: 'warning.light' }}>
                    <CardContent>
                      <Box display="flex" alignItems="center" justifyContent="space-between" mb={2}>
                        <Typography variant="h6" color="warning.main">
                          üö® {anomaly.type.replace('_', ' ')} Detected
                        </Typography>
                        <Box display="flex" gap={1}>
                          <Chip
                            label={`Severity: ${Math.round(anomaly.severity * 100)}%`}
                            color={getSeverityColor(anomaly.severity)}
                            size="small"
                          />
                          <Chip
                            label={`Confidence: ${Math.round(anomaly.confidence * 100)}%`}
                            variant="outlined"
                            size="small"
                          />
                        </Box>
                      </Box>

                      <Grid container spacing={2}>
                        <Grid item xs={12} md={6}>
                          <Typography variant="subtitle2" gutterBottom>
                            üìç Affected Resources:
                          </Typography>
                          <Box sx={{ mb: 2 }}>
                            {anomaly.affected_resources.map((resource: string, idx: number) => (
                              <Chip
                                key={idx}
                                label={resource}
                                variant="outlined"
                                size="small"
                                sx={{ mr: 1, mb: 1 }}
                              />
                            ))}
                          </Box>
                        </Grid>

                        <Grid item xs={12} md={6}>
                          <Typography variant="subtitle2" gutterBottom>
                            ‚è∞ Time Window:
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            From: {new Date(anomaly.time_window.start).toLocaleString()}
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            To: {new Date(anomaly.time_window.end).toLocaleString()}
                          </Typography>
                        </Grid>
                      </Grid>

                      {(anomaly as any).patterns && (anomaly as any).patterns.length > 0 && (
                        <Box mt={2}>
                          <Typography variant="subtitle2" gutterBottom>
                            üîç Anomaly Patterns:
                          </Typography>
                          {(anomaly as any).patterns.map((pattern: any, idx: number) => (
                            <Box key={idx} sx={{ mb: 1, p: 1, bgcolor: 'grey.50', borderRadius: 1 }}>
                              <Typography variant="body2">
                                <strong>{pattern.feature}:</strong> Expected {pattern.expected_range[0]}-{pattern.expected_range[1]}, 
                                Found {pattern.actual_value} (Deviation: {Math.round(pattern.deviation * 100)}%)
                              </Typography>
                            </Box>
                          ))}
                        </Box>
                      )}

                      <Box mt={2} p={2} sx={{ bgcolor: 'info.light', borderRadius: 1 }}>
                        <Typography variant="subtitle2" gutterBottom>
                          üí° Recommended Actions:
                        </Typography>
                        <Box>
                          {anomaly.type === 'PORT_SCAN' && (
                            <Box>
                              <Typography variant="body2">‚Ä¢ Review firewall rules and block suspicious IPs</Typography>
                              <Typography variant="body2">‚Ä¢ Enable intrusion detection systems</Typography>
                              <Typography variant="body2">‚Ä¢ Monitor for additional scanning attempts</Typography>
                            </Box>
                          )}
                          {anomaly.type === 'SUSPICIOUS_IP' && (
                            <Box>
                              <Typography variant="body2">‚Ä¢ Investigate the source IP for malicious activity</Typography>
                              <Typography variant="body2">‚Ä¢ Consider blocking the IP if confirmed malicious</Typography>
                              <Typography variant="body2">‚Ä¢ Review access logs for this IP</Typography>
                            </Box>
                          )}
                          {anomaly.type === 'BRUTE_FORCE' && (
                            <Box>
                              <Typography variant="body2">‚Ä¢ Implement account lockout policies</Typography>
                              <Typography variant="body2">‚Ä¢ Enable multi-factor authentication</Typography>
                              <Typography variant="body2">‚Ä¢ Monitor for successful logins from this IP</Typography>
                            </Box>
                          )}
                          {anomaly.type === 'UNUSUAL_API' && (
                            <Box>
                              <Typography variant="body2">‚Ä¢ Review API access patterns</Typography>
                              <Typography variant="body2">‚Ä¢ Validate API key usage</Typography>
                              <Typography variant="body2">‚Ä¢ Check for unauthorized API calls</Typography>
                            </Box>
                          )}
                        </Box>
                      </Box>
                    </CardContent>
                  </Card>
                ))}
              </Box>
            )}

            {uploadResult.anomalies.length === 0 && uploadResult.logs_processed > 0 && (
              <Alert severity="success" sx={{ mt: 2 }}>
                ‚úÖ No security anomalies detected in the analyzed logs. Your infrastructure appears to be secure!
              </Alert>
            )}
          </CardContent>
        </Card>
      )}
    </Box>
  );
};

export default LogUpload;
