import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Button,
  Chip,
  List,
  ListItem,
  ListItemText,
  Tabs,
  Tab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Divider,
  Grid,
  IconButton
} from '@mui/material';
import {
  CheckCircle,
  Cancel,
  Security,
  Rule,
  Warning,
  Info,
  Visibility,
  ThumbUp,
  ThumbDown,
  AutoFixHigh,
  Shield
} from '@mui/icons-material';
import apiClient from '../config/api';
import { SecurityRule, Severity } from '../types';

interface RulesData {
  active_rules: SecurityRule[];
  candidate_rules: SecurityRule[];
}

const RuleManagement: React.FC = () => {
  const [tabValue, setTabValue] = useState(0);
  const [rulesData, setRulesData] = useState<RulesData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedRule, setSelectedRule] = useState<SecurityRule | null>(null);
  const [dialogOpen, setDialogOpen] = useState(false);

  useEffect(() => {
    fetchRules();
  }, []);

  const fetchRules = async () => {
    try {
      setLoading(true);
      const response = await apiClient.get('/api/rules');
      setRulesData(response.data);
      setError(null);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to fetch rules');
    } finally {
      setLoading(false);
    }
  };

  const handleApproveRule = async (ruleId: string) => {
    try {
      await apiClient.post(`/api/rules/${ruleId}/approve`);
      await fetchRules(); // Refresh the rules list
      setDialogOpen(false);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to approve rule');
    }
  };

  const handleRejectRule = async (ruleId: string) => {
    try {
      await apiClient.post(`/api/rules/${ruleId}/reject`);
      await fetchRules(); // Refresh the rules list
      setDialogOpen(false);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to reject rule');
    }
  };

  const getSeverityIcon = (severity: Severity) => {
    switch (severity) {
      case Severity.CRITICAL:
        return <Warning sx={{ color: '#ff4d4d' }} />;
      case Severity.HIGH:
        return <Warning sx={{ color: '#ff9800' }} />;
      case Severity.MEDIUM:
        return <Info sx={{ color: '#2d5da1' }} />;
      case Severity.LOW:
        return <Info sx={{ color: '#4caf50' }} />;
      default:
        return <Info />;
    }
  };

  const getSeverityColor = (severity: Severity) => {
    switch (severity) {
      case Severity.CRITICAL:
        return 'error';
      case Severity.HIGH:
        return 'warning';
      case Severity.MEDIUM:
        return 'info';
      case Severity.LOW:
        return 'success';
      default:
        return 'default';
    }
  };

  const openRuleDialog = (rule: SecurityRule) => {
    setSelectedRule(rule);
    setDialogOpen(true);
  };

  const RuleList: React.FC<{ rules: SecurityRule[]; showActions?: boolean }> = ({ 
    rules, 
    showActions = false 
  }) => (
    <Box>
      {rules.map((rule, index) => (
        <Box 
          key={rule.id} 
          sx={{ 
            mb: 3,
            backgroundColor: '#ffffff',
            borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
            border: '3px solid #2d2d2d',
            boxShadow: '4px 4px 0px 0px #2d2d2d',
            transform: `rotate(${index % 2 === 0 ? '-0.5deg' : '0.5deg'})`,
            position: 'relative',
            '&:hover': {
              transform: `rotate(${index % 2 === 0 ? '0.5deg' : '-0.5deg'}) scale(1.01)`,
            },
            // Rule number badge
            '&::before': {
              content: `"${index + 1}"`,
              position: 'absolute',
              top: -10,
              left: -10,
              width: 30,
              height: 30,
              backgroundColor: '#ff4d4d',
              color: '#ffffff',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '0.9rem',
              fontFamily: '"Kalam", cursive',
              fontWeight: 700,
              border: '2px solid #2d2d2d',
              zIndex: 1,
            }
          }}
        >
          <Box sx={{ p: 4 }}>
            <Box display="flex" alignItems="center" gap={2} mb={2}>
              {/* Wobbly Severity Icon */}
              <Box
                sx={{
                  width: 50,
                  height: 50,
                  backgroundColor: getSeverityColor(rule.severity) === 'error' ? '#ff4d4d' : 
                                   getSeverityColor(rule.severity) === 'warning' ? '#ff9800' :
                                   getSeverityColor(rule.severity) === 'info' ? '#2d5da1' : '#4caf50',
                  borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
                  border: '3px solid #2d2d2d',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  boxShadow: '2px 2px 0px 0px #2d2d2d',
                }}
              >
                {getSeverityIcon(rule.severity)}
              </Box>

              <Box flex={1}>
                <Typography 
                  variant="h5" 
                  sx={{ 
                    color: '#2d2d2d',
                    fontFamily: '"Kalam", cursive',
                    fontWeight: 700,
                    mb: 1,
                  }}
                >
                  {rule.name}
                </Typography>

                <Box display="flex" gap={1} flexWrap="wrap">
                  <Chip
                    label={rule.severity}
                    color={getSeverityColor(rule.severity)}
                    size="small"
                    sx={{
                      borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
                      fontFamily: '"Patrick Hand", cursive',
                      border: '2px solid #2d2d2d',
                    }}
                  />
                  <Chip
                    label={rule.source}
                    variant="outlined"
                    size="small"
                    sx={{
                      borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
