import React, { useState, useCallback } from 'react';
import {
  Box,
  Button,
  Typography,
  TextField,
  Alert,
  Chip,
  Tabs,
  Tab,
  Grid,
  LinearProgress,
  Divider,
  Paper,
  IconButton,
  Collapse
} from '@mui/material';
import {
  CloudUpload,
  Code,
  Security,
  Warning,
  Info,
  CheckCircle,
  BugReport,
  Shield,
  Assessment,
  ExpandLess,
  ExpandMore as ExpandMoreIcon
} from '@mui/icons-material';
import { useDropzone } from 'react-dropzone';
import apiClient from '../config/api';
import { ScanResult, Severity } from '../types';

interface ScanResponse {
  message: string;
  files_scanned?: number;
  issues_found: number;
  results: ScanResult[];
}

interface SecurityInsight {
  category: string;
  count: number;
  severity: Severity;
  description: string;
}

const IaCScanner: React.FC = () => {
  const [tabValue, setTabValue] = useState(0);
  const [terraformCode, setTerraformCode] = useState('');
  const [scanning, setScanning] = useState(false);
  const [scanResult, setScanResult] = useState<ScanResponse | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [expandedResults, setExpandedResults] = useState<Set<number>>(new Set());

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    if (acceptedFiles.length === 0) return;

    setScanning(true);
    setError(null);
    setScanResult(null);

    try {
      const formData = new FormData();
      acceptedFiles.forEach(file => {
        formData.append('files', file);
      });

      const response = await apiClient.post('/api/iac/scan', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setScanResult(response.data);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to scan files');
    } finally {
      setScanning(false);
    }
  }, []);

  const handleTextScan = async () => {
    if (!terraformCode.trim()) {
      setError('Please enter Terraform code to scan');
      return;
    }

    setScanning(true);
    setError(null);
    setScanResult(null);

    try {
      const formData = new FormData();
      formData.append('terraform_code', terraformCode);

      const response = await apiClient.post('/api/iac/scan-text', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setScanResult(response.data);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to scan code');
    } finally {
      setScanning(false);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'text/plain': ['.tf', '.hcl'],
      'application/octet-stream': ['.tf', '.hcl']
    },
    multiple: true
  });

  const getSeverityIcon = (severity: Severity) => {
    switch (severity) {
      case Severity.CRITICAL:
        return <BugReport sx={{ color: '#d32f2f' }} />;
      case Severity.HIGH:
        return <Warning sx={{ color: '#f57c00' }} />;
      case Severity.MEDIUM:
        return <Info sx={{ color: '#1976d2' }} />;
      case Severity.LOW:
        return <Info sx={{ color: '#616161' }} />;
      default:
        return <Info sx={{ color: '#616161' }} />;
    }
  };

  const getSeverityColor = (severity: Severity): string => {
    switch (severity) {
      case Severity.CRITICAL:
        return '#d32f2f';
      case Severity.HIGH:
        return '#f57c00';
      case Severity.MEDIUM:
        return '#1976d2';
      case Severity.LOW:
        return '#616161';
      default:
        return '#616161';
    }
  };

  const getSeverityBackground = (severity: Severity): string => {
    switch (severity) {
      case Severity.CRITICAL:
        return '#ffebee';
      case Severity.HIGH:
        return '#fff3e0';
      case Severity.MEDIUM:
        return '#e3f2fd';
      case Severity.LOW:
        return '#f5f5f5';
      default:
        return '#f5f5f5';
    }
  };

  const groupResultsBySeverity = (results: ScanResult[]) => {
    const grouped = results.reduce((acc, result) => {
      if (!acc[result.severity]) {
        acc[result.severity] = [];
      }
      acc[result.severity].push(result);
      return acc;
    }, {} as Record<Severity, ScanResult[]>);

    // Sort by severity priority
    const severityOrder = [Severity.CRITICAL, Severity.HIGH, Severity.MEDIUM, Severity.LOW];
    return severityOrder.reduce((acc, severity) => {
      if (grouped[severity]) {
        acc[severity] = grouped[severity];
      }
      return acc;
    }, {} as Record<Severity, ScanResult[]>);
  };

  const getSecurityInsights = (results: ScanResult[]): SecurityInsight[] => {
    const insights: SecurityInsight[] = [];
    const grouped = groupResultsBySeverity(results);

    Object.entries(grouped).forEach(([severity, severityResults]) => {
      const categories = severityResults.reduce((acc, result) => {
        const category = result.rule_id.split('-')[0].toUpperCase();
        acc[category] = (acc[category] || 0) + 1;
        return acc;
      }, {} as Record<string, number>);

      Object.entries(categories).forEach(([category, count]) => {
        insights.push({
          category,
          count,
          severity: severity as Severity,
          description: getCategoryDescription(category)
        });
      });
    });

    return insights.sort((a, b) => {
      const severityWeight = { CRITICAL: 4, HIGH: 3, MEDIUM: 2, LOW: 1 };
      return (severityWeight[b.severity] || 0) - (severityWeight[a.severity] || 0);
    });
  };

  const getCategoryDescription = (category: string): string => {
    const descriptions: Record<string, string> = {
      'S3': 'Amazon S3 storage security configurations',
      'SG': 'Security Group network access controls',
      'EC2': 'Elastic Compute Cloud instance security',
      'RDS': 'Relational Database Service security',
      'IAM': 'Identity and Access Management policies',
      'VPC': 'Virtual Private Cloud network security',
      'KMS': 'Key Management Service encryption',
      'LAMBDA': 'AWS Lambda function security',
      'CLOUDTRAIL': 'CloudTrail logging and monitoring'
    };
    return descriptions[category] || `${category} security configurations`;
  };

  const toggleResultExpansion = (index: number) => {
    const newExpanded = new Set(expandedResults);
    if (newExpanded.has(index)) {
      newExpanded.delete(index);
    } else {
      newExpanded.add(index);
    }
    setExpandedResults(newExpanded);
  };

  return (
    <Box sx={{ minHeight: '100vh', p: 4 }}>
      {/* Art Deco Header Section */}
      <Box sx={{ textAlign: 'center', mb: 8, position: 'relative' }}>
        {/* Decorative Top Line */}
        <Box sx={{ 
          position: 'absolute',
          top: 0,
          left: '50%',
          transform: 'translateX(-50%)',
          width: 300,
          height: 4,
          background: 'linear-gradient(90deg, transparent, #D4AF37, transparent)',
        }} />

        {/* Diamond Icon */}
        <Box
          sx={{
            width: 100,
            height: 100,
            backgroundColor: '#D4AF37',
            transform: 'rotate(45deg)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            mx: 'auto',
            mb: 4,
            position: 'relative',
            boxShadow: '0 0 30px rgba(212, 175, 55, 0.3)',
            '&::before': {
              content: '""',
              position: 'absolute',
              top: 6,
              left: 6,
              right: 6,
              bottom: 6,
              border: '2px solid #0A0A0A',
            }
          }}
        >
          <Security 
            sx={{ 
              fontSize: 40, 
              color: '#0A0A0A',
              transform: 'rotate(-45deg)',
            }} 
          />
        </Box>

        <Typography 
          variant="h2" 
          sx={{ 
            color: '#D4AF37',
            mb: 2,
            textShadow: '0 0 20px rgba(212, 175, 55, 0.3)',
          }}
        >
          Infrastructure Scanner
        </Typography>
        <Typography 
          variant="h5" 
          sx={{ 
            color: '#888888',
            textTransform: 'uppercase',
            letterSpacing: '0.2em',
            mb: 4,
          }}
        >
          Terraform Security Analysis
        </Typography>
        <Typography 
          variant="body1" 
          sx={{ 
            color: '#F2F0E4',
            maxWidth: 800,
            mx: 'auto',
            lineHeight: 1.8,
            fontSize: '1.125rem',
          }}
        >
          Sophisticated security analysis for infrastructure configurations with architectural precision and detailed vulnerability assessment
        </Typography>
      </Box>

      {/* Input Section */}
      <Paper elevation={1} sx={{ mb: 4, border: '1px solid #e0e0e0' }}>
        <Box sx={{ borderBottom: '1px solid #e0e0e0' }}>
          <Tabs 
            value={tabValue} 
            onChange={(_, newValue) => setTabValue(newValue)}
            sx={{
              '& .MuiTab-root': {
                minHeight: 64,
                textTransform: 'none',
                fontSize: '1rem',
                fontWeight: 500
              }
            }}
          >
            <Tab 
              icon={<CloudUpload />} 
              label="Upload Terraform Files" 
              iconPosition="start"
            />
            <Tab 
              icon={<Code />} 
              label="Analyze Code Snippet" 
              iconPosition="start"
            />
          </Tabs>
        </Box>

        <Box sx={{ p: 3 }}>
          {tabValue === 0 && (
            <Box
              {...getRootProps()}
              sx={{
                border: '2px dashed #bdbdbd',
                borderColor: isDragActive ? '#1976d2' : '#bdbdbd',
                borderRadius: 2,
                p: 4,
                textAlign: 'center',
                cursor: 'pointer',
                bgcolor: isDragActive ? '#f5f5f5' : 'transparent',
                transition: 'all 0.2s ease',
                '&:hover': {
                  bgcolor: '#f9f9f9',
                  borderColor: '#757575',
                },
              }}
            >
              <input {...getInputProps()} />
              <CloudUpload sx={{ fontSize: 56, color: '#757575', mb: 2 }} />
              <Typography variant="h6" sx={{ mb: 1, color: '#424242' }}>
                {isDragActive ? 'Drop your Terraform files here' : 'Upload Terraform Configuration Files'}
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                Drag and drop files here, or click to browse
              </Typography>
              <Typography variant="caption" color="text.secondary">
                Supported: .tf, .hcl files  Multiple files supported
              </Typography>
            </Box>
          )}

          {tabValue === 1 && (
            <Box>
              <Typography variant="subtitle1" sx={{ mb: 2, fontWeight: 500 }}>
                Paste Terraform Configuration
              </Typography>
              <TextField
                fullWidth
                multiline
                rows={14}
                variant="outlined"
                placeholder={`# Paste your Terraform code here for analysis
# Example:
resource "aws_s3_bucket" "example" {
  bucket = "my-example-bucket"

  tags = {
    Name = "Example bucket"
  }
}`}
                value={terraformCode}
                onChange={(e) => setTerraformCode(e.target.value)}
                sx={{
                  mb: 3,
                  '& .MuiInputBase-input': {
                    fontFamily: '"Fira Code", "Monaco", "Consolas", monospace',
                    fontSize: '0.875rem',
                    lineHeight: 1.5,
                  },
                  '& .MuiOutlinedInput-root': {
                    bgcolor: '#fafafa',
                  }
                }}
              />
              <Button
                variant="contained"
                size="large"
                startIcon={<Security />}
                onClick={handleTextScan}
                disabled={scanning || !terraformCode.trim()}
                sx={{
                  px: 4,
                  py: 1.5,
                  textTransform: 'none',
                  fontSize: '1rem',
                  fontWeight: 500
                }}
              >
                Analyze Configuration
              </Button>
            </Box>
          )}
        </Box>
      </Paper>

      {/* Scanning Progress */}
      {scanning && (
        <Paper elevation={1} sx={{ mb: 4, p: 3, border: '1px solid #e0e0e0' }}>
          <Box display="flex" alignItems="center" gap={2} mb={2}>
            <Security color="primary" />
            <Typography variant="h6">Security Analysis in Progress</Typography>
          </Box>
          <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
            Scanning Terraform configurations against 150+ security rules...
          </Typography>
          <LinearProgress sx={{ height: 6, borderRadius: 3 }} />
        </Paper>
      )}

      {/* Error Display */}
      {error && (
        <Alert 
          severity="error" 
          sx={{ 
            mb: 4,
            '& .MuiAlert-message': { fontSize: '1rem' }
          }}
        >
          {error}
        </Alert>
      )}

      {/* Results Section */}
      {scanResult && (
        <Box>
          {/* Summary Cards */}
          <Grid container spacing={3} sx={{ mb: 4 }}>
            <Grid item xs={12} md={4}>
              <Paper elevation={1} sx={{ p: 3, textAlign: 'center', border: '1px solid #e0e0e0' }}>
                <Assessment sx={{ fontSize: 40, color: '#1976d2', mb: 1 }} />
                <Typography variant="h4" sx={{ fontWeight: 600, color: '#1976d2' }}>
                  {scanResult.files_scanned || 1}
                </Typography>
                <Typography variant="body1" color="text.secondary">
                  Files Analyzed
                </Typography>
              </Paper>
            </Grid>
            <Grid item xs={12} md={4}>
              <Paper elevation={1} sx={{ p: 3, textAlign: 'center', border: '1px solid #e0e0e0' }}>
                <BugReport sx={{ 
                  fontSize: 40, 
                  color: scanResult.issues_found > 0 ? '#f57c00' : '#4caf50', 
                  mb: 1 
                }} />
                <Typography variant="h4" sx={{ 
                  fontWeight: 600, 
                  color: scanResult.issues_found > 0 ? '#f57c00' : '#4caf50' 
                }}>
                  {scanResult.issues_found}
                </Typography>
                <Typography variant="body1" color="text.secondary">
                  Security Issues
                </Typography>
              </Paper>
            </Grid>
            <Grid item xs={12} md={4}>
              <Paper elevation={1} sx={{ p: 3, textAlign: 'center', border: '1px solid #e0e0e0' }}>
                <Shield sx={{ fontSize: 40, color: '#4caf50', mb: 1 }} />
                <Typography variant="h4" sx={{ fontWeight: 600, color: '#4caf50' }}>
                  150+
                </Typography>
                <Typography variant="body1" color="text.secondary">
                  Rules Applied
                </Typography>
              </Paper>
            </Grid>
          </Grid>

          {scanResult.issues_found === 0 ? (
            <Paper elevation={1} sx={{ p: 4, textAlign: 'center', border: '1px solid #e0e0e0' }}>
              <CheckCircle sx={{ fontSize: 64, color: '#4caf50', mb: 2 }} />
              <Typography variant="h5" sx={{ fontWeight: 600, color: '#4caf50', mb: 1 }}>
                Security Scan Complete - No Issues Found
              </Typography>
              <Typography variant="body1" color="text.secondary">
                Your Terraform configuration follows security best practices. All 150+ security rules passed successfully.
              </Typography>
            </Paper>
          ) : (
            <Box>
              {/* Security Insights */}
              <Paper elevation={1} sx={{ mb: 4, border: '1px solid #e0e0e0' }}>
                <Box sx={{ p: 3, borderBottom: '1px solid #e0e0e0' }}>
                  <Typography variant="h6" sx={{ fontWeight: 600 }}>
                    Security Analysis Overview
                  </Typography>
                </Box>
                <Box sx={{ p: 3 }}>
                  <Grid container spacing={2}>
                    {getSecurityInsights(scanResult.results).map((insight, index) => (
                      <Grid item xs={12} sm={6} md={4} key={index}>
                        <Box
                          sx={{
                            p: 2,
                            border: `1px solid ${getSeverityColor(insight.severity)}`,
                            borderRadius: 1,
                            bgcolor: getSeverityBackground(insight.severity)
                          }}
                        >
                          <Box display="flex" alignItems="center" gap={1} mb={1}>
                            {getSeverityIcon(insight.severity)}
                            <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                              {insight.category}
                            </Typography>
                            <Chip 
                              label={insight.count} 
                              size="small" 
                              sx={{ 
                                bgcolor: getSeverityColor(insight.severity),
                                color: 'white',
                                fontWeight: 600
                              }} 
                            />
                          </Box>
                          <Typography variant="body2" color="text.secondary">
                            {insight.description}
                          </Typography>
                        </Box>
                      </Grid>
                    ))}
                  </Grid>
                </Box>
              </Paper>

              {/* Detailed Results */}
              <Paper elevation={1} sx={{ border: '1px solid #e0e0e0' }}>
                <Box sx={{ p: 3, borderBottom: '1px solid #e0e0e0' }}>
                  <Typography variant="h6" sx={{ fontWeight: 600 }}>
                    Detailed Security Findings
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Click on any finding to view detailed information and remediation steps
                  </Typography>
                </Box>

                <Box sx={{ p: 0 }}>
                  {Object.entries(groupResultsBySeverity(scanResult.results)).map(([severity, results]) => (
                    <Box key={severity}>
                      <Box sx={{ 
                        p: 2, 
                        bgcolor: getSeverityBackground(severity as Severity),
                        borderBottom: '1px solid #e0e0e0'
                      }}>
                        <Box display="flex" alignItems="center" gap={2}>
                          {getSeverityIcon(severity as Severity)}
                          <Typography variant="h6" sx={{ fontWeight: 600 }}>
                            {severity} Priority Issues
                          </Typography>
                          <Chip 
                            label={`${results.length} findings`}
                            sx={{ 
                              bgcolor: getSeverityColor(severity as Severity),
                              color: 'white',
                              fontWeight: 600
                            }}
                          />
                        </Box>
                      </Box>

                      {results.map((result, index) => {
                        const globalIndex = scanResult.results.indexOf(result);
                        const isExpanded = expandedResults.has(globalIndex);

                        return (
                          <Box key={globalIndex} sx={{ borderBottom: '1px solid #f0f0f0' }}>
                            <Box
                              sx={{
                                p: 3,
                                cursor: 'pointer',
                                '&:hover': { bgcolor: '#fafafa' },
                                transition: 'background-color 0.2s'
                              }}
                              onClick={() => toggleResultExpansion(globalIndex)}
                            >
                              <Box display="flex" alignItems="flex-start" justifyContent="space-between">
                                <Box flex={1}>
                                  <Box display="flex" alignItems="center" gap={2} mb={1}>
                                    <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                                      {result.description}
                                    </Typography>
                                    <Chip 
                                      label={result.rule_id}
                                      size="small"
                                      variant="outlined"
                                    />
                                  </Box>
                                  <Typography variant="body2" color="text.secondary">
                                    {result.file_path}  Line {result.line_number}
                                  </Typography>
                                </Box>
                                <IconButton size="small">
                                  {isExpanded ? <ExpandLess /> : <ExpandMoreIcon />}
                                </IconButton>
                              </Box>
                            </Box>

                            <Collapse in={isExpanded}>
                              <Box sx={{ p: 3, pt: 0, bgcolor: '#fafafa' }}>
                                <Divider sx={{ mb: 2 }} />
                                <Grid container spacing={3}>
                                  <Grid item xs={12} md={6}>
                                    <Typography variant="subtitle2" sx={{ fontWeight: 600, mb: 1 }}>
                                      Security Impact
                                    </Typography>
                                    <Typography variant="body2" sx={{ mb: 2 }}>
                                      This {severity.toLowerCase()} priority issue could expose your infrastructure to security risks.
                                    </Typography>

                                    <Typography variant="subtitle2" sx={{ fontWeight: 600, mb: 1 }}>
                                      Location Details
                                    </Typography>
                                    <Box sx={{ bgcolor: 'white', p: 2, borderRadius: 1, border: '1px solid #e0e0e0' }}>
                                      <Typography variant="body2" sx={{ fontFamily: 'monospace' }}>
                                        File: {result.file_path}<br />
                                        Line: {result.line_number}<br />
                                        Rule: {result.rule_id}
                                      </Typography>
                                    </Box>
                                  </Grid>

                                  <Grid item xs={12} md={6}>
                                    <Typography variant="subtitle2" sx={{ fontWeight: 600, mb: 1 }}>
                                      Remediation Steps
                                    </Typography>
                                    <Box sx={{ bgcolor: 'white', p: 2, borderRadius: 1, border: '1px solid #e0e0e0' }}>
                                      <Typography variant="body2">
                                        {result.remediation}
                                      </Typography>
                                    </Box>

                                    <Typography variant="subtitle2" sx={{ fontWeight: 600, mt: 2, mb: 1 }}>
                                      Compliance Impact
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                      This finding may affect compliance with security frameworks like SOC2, PCI-DSS, and GDPR.
                                    </Typography>
                                  </Grid>
                                </Grid>
                              </Box>
                            </Collapse>
                          </Box>
                        );
                      })}
                    </Box>
                  ))}
                </Box>
              </Paper>
            </Box>
          )}
        </Box>
      )}
    </Box>
  );
};

export default IaCScanner;
