import React, { useState } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  Box,
  Button,
  TextField,
  Chip,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Tabs,
  Tab,
} from '@mui/material';
import {
  Add,
  Edit,
  Delete,
  Visibility,
  CheckCircle,
  Cancel,
  Security,
  Rule as RuleIcon,
} from '@mui/icons-material';

interface SecurityRule {
  id: string;
  name: string;
  description: string;
  severity: 'critical' | 'high' | 'medium' | 'low';
  category: string;
  status: 'active' | 'inactive' | 'pending';
  created_at: string;
}

const Rules: React.FC = () => {
  const [tabValue, setTabValue] = useState(0);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedRule, setSelectedRule] = useState<SecurityRule | null>(null);

  const mockRules: SecurityRule[] = [
    {
      id: 's3-001',
      name: 'S3 Bucket Public Read Access',
      description: 'S3 bucket should not have public-read ACL',
      severity: 'high',
      category: 'S3',
      status: 'active',
      created_at: '2024-01-15',
    },
    {
      id: 'sg-001',
      name: 'Security Group SSH Open to World',
      description: 'Security group should not allow SSH (port 22) from 0.0.0.0/0',
      severity: 'critical',
      category: 'Security Groups',
      status: 'active',
      created_at: '2024-01-14',
    },
    {
      id: 'iam-001',
      name: 'IAM Policy Wildcard Actions',
      description: 'IAM policy should not use wildcard (*) actions',
      severity: 'high',
      category: 'IAM',
      status: 'pending',
      created_at: '2024-01-13',
    },
  ];

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical': return '#ef4444';
      case 'high': return '#f59e0b';
      case 'medium': return '#06b6d4';
      case 'low': return '#10b981';
      default: return '#6b7280';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return '#10b981';
      case 'inactive': return '#6b7280';
      case 'pending': return '#f59e0b';
      default: return '#6b7280';
    }
  };

  const handleViewRule = (rule: SecurityRule) => {
    setSelectedRule(rule);
    setOpenDialog(true);
  };

  const filteredRules = mockRules.filter(rule => {
    if (tabValue === 0) return true; // All
    if (tabValue === 1) return rule.status === 'active';
    if (tabValue === 2) return rule.status === 'pending';
    if (tabValue === 3) return rule.status === 'inactive';
    return true;
  });

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      {/* Header */}
      <Box sx={{ mb: 6, textAlign: 'center' }}>
        <Typography variant="h1" sx={{ mb: 2 }}>
          Security Rules
        </Typography>
        <Typography variant="body1" sx={{ fontSize: '1.2rem', mb: 4, maxWidth: 600, mx: 'auto' }}>
          Manage and configure security rules for infrastructure scanning
        </Typography>
        <Button
          variant="contained"
          size="large"
          startIcon={<Add />}
          sx={{ px: 4 }}
        >
          Create New Rule
        </Button>
      </Box>

      <Grid container spacing={4}>
        {/* Stats Cards */}
        <Grid item xs={12}>
          <Grid container spacing={3} sx={{ mb: 4 }}>
            <Grid item xs={12} sm={6} md={3}>
              <Card>
                <CardContent sx={{ textAlign: 'center', p: 3 }}>
                  <Security sx={{ fontSize: 40, color: '#6366f1', mb: 2 }} />
                  <Typography variant="h3" sx={{ color: '#6366f1', mb: 1 }}>
                    150
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Total Rules
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Card>
                <CardContent sx={{ textAlign: 'center', p: 3 }}>
                  <CheckCircle sx={{ fontSize: 40, color: '#10b981', mb: 2 }} />
                  <Typography variant="h3" sx={{ color: '#10b981', mb: 1 }}>
                    142
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Active Rules
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Card>
                <CardContent sx={{ textAlign: 'center', p: 3 }}>
                  <Cancel sx={{ fontSize: 40, color: '#f59e0b', mb: 2 }} />
                  <Typography variant="h3" sx={{ color: '#f59e0b', mb: 1 }}>
                    5
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Pending Review
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Card>
                <CardContent sx={{ textAlign: 'center', p: 3 }}>
                  <RuleIcon sx={{ fontSize: 40, color: '#06b6d4', mb: 2 }} />
                  <Typography variant="h3" sx={{ color: '#06b6d4', mb: 1 }}>
                    3
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Inactive Rules
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </Grid>

        {/* Rules Table */}
        <Grid item xs={12}>
          <Card>
            <CardContent sx={{ p: 3 }}>
              <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
                <Tabs value={tabValue} onChange={(e, newValue) => setTabValue(newValue)}>
                  <Tab label="All Rules" />
                  <Tab label="Active" />
                  <Tab label="Pending" />
                  <Tab label="Inactive" />
                </Tabs>
              </Box>

              <TableContainer component={Paper} sx={{ backgroundColor: 'transparent' }}>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Rule ID</TableCell>
                      <TableCell>Name</TableCell>
                      <TableCell>Category</TableCell>
                      <TableCell>Severity</TableCell>
                      <TableCell>Status</TableCell>
                      <TableCell>Created</TableCell>
                      <TableCell>Actions</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {filteredRules.map((rule) => (
                      <TableRow
                        key={rule.id}
                        sx={{
                          '&:hover': {
                            backgroundColor: 'rgba(255, 255, 255, 0.02)',
                          },
                        }}
                      >
                        <TableCell>
                          <Typography variant="body2" sx={{ fontFamily: 'monospace', fontWeight: 600 }}>
                            {rule.id}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2" sx={{ fontWeight: 500 }}>
                            {rule.name}
                          </Typography>
                          <Typography variant="caption" color="text.secondary">
                            {rule.description.length > 50 
                              ? `${rule.description.substring(0, 50)}...` 
                              : rule.description}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Chip
                            label={rule.category}
                            size="small"
                            sx={{
                              backgroundColor: 'rgba(99, 102, 241, 0.1)',
                              color: '#6366f1',
                              border: 'none',
                            }}
                          />
                        </TableCell>
                        <TableCell>
                          <Chip
                            label={rule.severity.toUpperCase()}
                            size="small"
                            sx={{
                              backgroundColor: `${getSeverityColor(rule.severity)}20`,
                              color: getSeverityColor(rule.severity),
                              border: 'none',
                              fontWeight: 600,
                            }}
                          />
                        </TableCell>
                        <TableCell>
                          <Chip
                            label={rule.status.toUpperCase()}
                            size="small"
                            sx={{
                              backgroundColor: `${getStatusColor(rule.status)}20`,
                              color: getStatusColor(rule.status),
                              border: 'none',
                              fontWeight: 600,
                            }}
                          />
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2" color="text.secondary">
                            {rule.created_at}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Box sx={{ display: 'flex', gap: 1 }}>
                            <IconButton
                              size="small"
                              onClick={() => handleViewRule(rule)}
                              sx={{ '&:hover': { backgroundColor: 'rgba(99, 102, 241, 0.1)' } }}
                            >
                              <Visibility fontSize="small" />
                            </IconButton>
                            <IconButton
                              size="small"
                              sx={{ '&:hover': { backgroundColor: 'rgba(245, 158, 11, 0.1)' } }}
                            >
                              <Edit fontSize="small" />
                            </IconButton>
                            <IconButton
                              size="small"
                              sx={{ '&:hover': { backgroundColor: 'rgba(239, 68, 68, 0.1)' } }}
                            >
                              <Delete fontSize="small" />
                            </IconButton>
                          </Box>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Rule Details Dialog */}
      <Dialog
        open={openDialog}
        onClose={() => setOpenDialog(false)}
        maxWidth="md"
        fullWidth
        PaperProps={{
          sx: {
            background: 'rgba(26, 26, 46, 0.9)',
            backdropFilter: 'blur(20px)',
            border: '1px solid rgba(255, 255, 255, 0.1)',
          },
        }}
      >
        <DialogTitle>
          <Typography variant="h4">Rule Details</Typography>
        </DialogTitle>
        <DialogContent>
          {selectedRule && (
            <Box sx={{ pt: 2 }}>
              <Grid container spacing={3}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Rule ID"
                    value={selectedRule.id}
                    InputProps={{ readOnly: true }}
                    sx={{ mb: 2 }}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Category"
                    value={selectedRule.category}
                    InputProps={{ readOnly: true }}
                    sx={{ mb: 2 }}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Name"
                    value={selectedRule.name}
                    InputProps={{ readOnly: true }}
                    sx={{ mb: 2 }}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    multiline
                    rows={3}
                    label="Description"
                    value={selectedRule.description}
                    InputProps={{ readOnly: true }}
                    sx={{ mb: 2 }}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <FormControl fullWidth>
                    <InputLabel>Severity</InputLabel>
                    <Select
                      value={selectedRule.severity}
                      label="Severity"
                      disabled
                    >
                      <MenuItem value="low">Low</MenuItem>
                      <MenuItem value="medium">Medium</MenuItem>
                      <MenuItem value="high">High</MenuItem>
                      <MenuItem value="critical">Critical</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <FormControl fullWidth>
                    <InputLabel>Status</InputLabel>
                    <Select
                      value={selectedRule.status}
                      label="Status"
                      disabled
                    >
                      <MenuItem value="active">Active</MenuItem>
                      <MenuItem value="inactive">Inactive</MenuItem>
                      <MenuItem value="pending">Pending</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
              </Grid>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Close</Button>
          <Button variant="contained">Edit Rule</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Rules;
