import React, { useState, useCallback } from 'react';
import {
  Box,
  Button,
  Typography,
  Card,
  CardContent,
  TextField,
  Alert,
  List,
  ListItem,
  ListItemText,
  Chip,
  Tabs,
  Tab,
  Grid,
  LinearProgress,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Divider,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Tooltip,
  IconButton,
  Collapse
} from '@mui/material';
import {
  CloudUpload,
  Code,
  Security,
  Warning,
  Info,
  CheckCircle,
  ExpandMore,
  BugReport,
  Shield,
  Assessment,
  FileCopy,
  ExpandLess,
  ExpandMore as ExpandMoreIcon
} from '@mui/icons-material';
import { useDropzone } from 'react-dropzone';
import apiClient from '../config/api';
import { ScanResult, Severity } from '../types';

interface ScanResponse {
  message: string;
  files_scanned?: number;
  issues_found: number;
  results: ScanResult[];
}

const IaCScanner: React.FC = () => {
  const [tabValue, setTabValue] = useState(0);
  const [terraformCode, setTerraformCode] = useState('');
  const [scanning, setScanning] = useState(false);
  const [scanResult, setScanResult] = useState<ScanResponse | null>(null);
  const [error, setError] = useState<string | null>(null);

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    if (acceptedFiles.length === 0) return;

    setScanning(true);
    setError(null);
    setScanResult(null);

    try {
      const formData = new FormData();
      acceptedFiles.forEach(file => {
        formData.append('files', file);
      });

      const response = await apiClient.post('/api/iac/scan', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setScanResult(response.data);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to scan files');
    } finally {
      setScanning(false);
    }
  }, []);

  const handleTextScan = async () => {
    if (!terraformCode.trim()) {
      setError('Please enter Terraform code to scan');
      return;
    }

    setScanning(true);
    setError(null);
    setScanResult(null);

    try {
      const formData = new FormData();
      formData.append('terraform_code', terraformCode);

      const response = await apiClient.post('/api/iac/scan-text', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setScanResult(response.data);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to scan code');
    } finally {
      setScanning(false);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'text/plain': ['.tf', '.hcl'],
      'application/octet-stream': ['.tf', '.hcl']
    },
    multiple: true
  });

  const getSeverityIcon = (severity: Severity) => {
    switch (severity) {
      case Severity.CRITICAL:
        return <Warning color="error" />;
      case Severity.HIGH:
        return <Warning color="warning" />;
      case Severity.MEDIUM:
        return <Info color="info" />;
      case Severity.LOW:
        return <Info color="action" />;
      default:
        return <Info />;
    }
  };

  const getSeverityColor = (severity: Severity) => {
    switch (severity) {
      case Severity.CRITICAL:
        return 'error';
      case Severity.HIGH:
        return 'warning';
      case Severity.MEDIUM:
        return 'info';
      case Severity.LOW:
        return 'default';
      default:
        return 'default';
    }
  };

  const groupResultsBySeverity = (results: ScanResult[]) => {
    const grouped = results.reduce((acc, result) => {
      if (!acc[result.severity]) {
        acc[result.severity] = [];
      }
      acc[result.severity].push(result);
      return acc;
    }, {} as Record<Severity, ScanResult[]>);

    // Sort by severity priority
    const severityOrder = [Severity.CRITICAL, Severity.HIGH, Severity.MEDIUM, Severity.LOW];
    return severityOrder.reduce((acc, severity) => {
      if (grouped[severity]) {
        acc[severity] = grouped[severity];
      }
      return acc;
    }, {} as Record<Severity, ScanResult[]>);
  };

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Infrastructure as Code Scanner
      </Typography>

      <Card sx={{ mb: 3 }}>
        <CardContent>
          <Tabs value={tabValue} onChange={(_, newValue) => setTabValue(newValue)}>
            <Tab icon={<CloudUpload />} label="Upload Files" />
            <Tab icon={<Code />} label="Paste Code" />
          </Tabs>

          <Box sx={{ mt: 2 }}>
            {tabValue === 0 && (
              <Box
                {...getRootProps()}
                sx={{
                  border: '2px dashed',
                  borderColor: isDragActive ? 'primary.main' : 'grey.300',
                  borderRadius: 2,
                  p: 4,
                  textAlign: 'center',
                  cursor: 'pointer',
                  bgcolor: isDragActive ? 'action.hover' : 'background.paper',
                  '&:hover': {
                    bgcolor: 'action.hover',
                  },
                }}
              >
                <input {...getInputProps()} />
                <CloudUpload sx={{ fontSize: 48, color: 'grey.500', mb: 2 }} />
                <Typography variant="h6" gutterBottom>
                  {isDragActive ? 'Drop the files here' : 'Drag & drop Terraform files here'}
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  or click to select files
                </Typography>
                <Typography variant="caption" color="text.secondary">
                  Supported formats: .tf, .hcl
                </Typography>
              </Box>
            )}

            {tabValue === 1 && (
              <Box>
                <TextField
                  fullWidth
                  multiline
                  rows={12}
                  variant="outlined"
                  placeholder="Paste your Terraform code here..."
                  value={terraformCode}
                  onChange={(e) => setTerraformCode(e.target.value)}
                  sx={{
                    mb: 2,
                    '& .MuiInputBase-input': {
                      fontFamily: 'monospace',
                      fontSize: '0.875rem',
                    },
                  }}
                />
                <Button
                  variant="contained"
                  startIcon={<Security />}
                  onClick={handleTextScan}
                  disabled={scanning || !terraformCode.trim()}
                >
                  Scan Code
                </Button>
              </Box>
            )}
          </Box>
        </CardContent>
      </Card>

      {scanning && (
        <Box sx={{ mb: 3 }}>
          <Typography variant="body2" gutterBottom>
            Scanning for security misconfigurations...
          </Typography>
          <LinearProgress />
        </Box>
      )}

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {scanResult && (
        <Card>
          <CardContent>
            <Box display="flex" alignItems="center" gap={1} mb={2}>
              {scanResult.issues_found === 0 ? (
                <CheckCircle color="success" />
              ) : (
                <Warning color="warning" />
              )}
              <Typography variant="h6">
                Scan Results
              </Typography>
            </Box>

            <Grid container spacing={2} sx={{ mb: 3 }}>
              {scanResult.files_scanned && (
                <Grid item xs={12} sm={4}>
                  <Typography variant="h6" color="primary">
                    {scanResult.files_scanned}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Files Scanned
                  </Typography>
                </Grid>
              )}
              <Grid item xs={12} sm={4}>
                <Typography variant="h6" color={scanResult.issues_found > 0 ? "warning.main" : "success.main"}>
                  {scanResult.issues_found}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Issues Found
                </Typography>
              </Grid>
            </Grid>

            {scanResult.issues_found === 0 ? (
              <Alert severity="success">
                No security issues found! Your Terraform code looks good.
              </Alert>
            ) : (
              <Box>
                <Typography variant="h6" gutterBottom>
                  Security Issues
                </Typography>

                {Object.entries(groupResultsBySeverity(scanResult.results)).map(([severity, results]) => (
                  <Box key={severity} sx={{ mb: 2 }}>
                    <Typography variant="subtitle1" gutterBottom>
                      <Chip
                        label={`${severity} (${results.length})`}
                        color={getSeverityColor(severity as Severity)}
                        size="small"
                        sx={{ mr: 1 }}
                      />
                    </Typography>

                    <List dense>
                      {results.map((result, index) => (
                        <ListItem key={index} divider>
                          <ListItemText
                            primary={
                              <Box display="flex" alignItems="center" gap={1}>
                                {getSeverityIcon(result.severity)}
                                <Typography variant="subtitle2">
                                  {result.description}
                                </Typography>
                              </Box>
                            }
                            secondary={
                              <Box>
                                <Typography variant="body2" color="text.secondary">
                                  File: {result.file_path} (Line {result.line_number})
                                </Typography>
                                <Typography variant="body2" color="text.secondary">
                                  Rule: {result.rule_id}
                                </Typography>
                                <Typography variant="body2" color="success.main" sx={{ mt: 1 }}>
                                  <strong>Remediation:</strong> {result.remediation}
                                </Typography>
                              </Box>
                            }
                          />
                        </ListItem>
                      ))}
                    </List>
                  </Box>
                ))}
              </Box>
            )}
          </CardContent>
        </Card>
      )}
    </Box>
  );
};

export default IaCScanner;
