sourcesContent:
import React, { useState, useCallback, useEffect } from 'react';
import {
  Box,
  Typography,
  LinearProgress,
  Alert,
  Chip,
  Grid,
  Paper,
  Stepper,
  Step,
  StepLabel,
  StepContent,
  Fade,
  Zoom,
  Slide,
  CircularProgress,
  Avatar,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Divider,
  IconButton,
  Collapse,
  Badge
} from '@mui/material';
import { 
  CloudUpload, 
  CheckCircle, 
  Error, 
  BugReport,
  Psychology,
  Analytics,
  Security,
  ExpandMore,
  ExpandLess,
  TrendingUp,
  Warning,
  Shield,
  Speed,
  AutoFixHigh,
  SmartToy,
  DataUsage,
  NetworkCheck,
  Assessment
} from '@mui/icons-material';
import { useDropzone } from 'react-dropzone';
import apiClient from '../config/api';
// Types defined inline

interface UploadResult {
  message: string;
  logs_processed: number;
  anomalies_detected: number;
  candidate_rules_generated: number;
  anomalies: any[];
}

interface MLProcessingStep {
  id: string;
  label: string;
  description: string;
  icon: React.ReactNode;
  completed: boolean;
  active: boolean;
  duration?: number;
}

const LogUpload: React.FC = () => {
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadResult, setUploadResult] = useState<UploadResult | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [processingSteps, setProcessingSteps] = useState<MLProcessingStep[]>([]);
  const [activeStep, setActiveStep] = useState(0);
  const [showMLInsights, setShowMLInsights] = useState(false);
  const [expandedAnomalies, setExpandedAnomalies] = useState<Set<number>>(new Set());
  const [animationTrigger, setAnimationTrigger] = useState(0);

  const initializeMLSteps = useCallback(() => {
    const steps: MLProcessingStep[] = [
      {
        id: 'upload',
        label: 'Data Ingestion',
        description: 'Uploading and parsing log files',
        icon: <CloudUpload />,
        completed: false,
        active: false
      },
      {
        id: 'preprocessing',
        label: 'Data Preprocessing',
        description: 'Normalizing and structuring log data',
        icon: <DataUsage />,
        completed: false,
        active: false,
        duration: 2000
      },
      {
        id: 'feature_extraction',
        label: 'Feature Extraction',
        description: 'Extracting behavioral patterns and features',
        icon: <Analytics />,
        completed: false,
        active: false,
        duration: 3000
      },
      {
        id: 'ml_analysis',
        label: 'ML Analysis',
        description: 'Running anomaly detection algorithms',
        icon: <SmartToy />,
        completed: false,
        active: false,
        duration: 4000
      },
      {
        id: 'pattern_recognition',
        label: 'Pattern Recognition',
        description: 'Identifying security threats and anomalies',
        icon: <Psychology />,
        completed: false,
        active: false,
        duration: 2500
      },
      {
        id: 'rule_generation',
        label: 'Rule Generation',
        description: 'Generating security rules from findings',
        icon: <AutoFixHigh />,
        completed: false,
        active: false,
        duration: 1500
      }
    ];
    setProcessingSteps(steps);
    return steps;
  }, []);

  const simulateMLProcessing = useCallback(async (steps: MLProcessingStep[]) => {
    for (let i = 0; i < steps.length; i++) {
      setActiveStep(i);

      // Update step to active
      setProcessingSteps(prev => prev.map((step, idx) => ({
        ...step,
        active: idx === i,
        completed: idx < i
      })));

      // Wait for step duration
      if (steps[i].duration) {
        await new Promise(resolve => setTimeout(resolve, steps[i].duration));
      }

      // Complete the step
      setProcessingSteps(prev => prev.map((step, idx) => ({
        ...step,
        active: false,
        completed: idx <= i
      })));
    }
  }, []);

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    if (acceptedFiles.length === 0) return;

    console.log('ðŸ“ Files dropped:', acceptedFiles.map(f => f.name));

    setUploading(true);
    setUploadProgress(0);
    setError(null);
    setUploadResult(null);
    setShowMLInsights(false);
    setAnimationTrigger(prev => prev + 1);

    // Initialize ML processing steps
    const steps = initializeMLSteps();

    try {
      const formData = new FormData();
      acceptedFiles.forEach(file => {
        formData.append('files', file);
        console.log(`ðŸ“Ž Added file: ${file.name} (${file.size} bytes)`);
      });

      // Start ML processing simulation
      const processingPromise = simulateMLProcessing(steps);

      console.log('ðŸš€ Sending request to backend...');
      const response = await apiClient.post('/api/logs/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        onUploadProgress: (progressEvent) => {
          if (progressEvent.total) {
            const progress = Math.round((progressEvent.loaded * 100) / progressEvent.total);
            setUploadProgress(progress);
            console.log(`ðŸ“Š Upload progress: ${progress}%`);
          }
        },
      });

      // Wait for both upload and ML processing simulation
      await processingPromise;

      console.log('âœ… Upload successful:', response.data);
      setUploadResult(response.data);

      // Show ML insights with delay for dramatic effect
      setTimeout(() => {
        setShowMLInsights(true);
      }, 1000);

    } catch (err: any) {
      console.error('âŒ Upload failed:', err);
      setError(err.response?.data?.detail || 'Failed to upload logs');
    } finally {
      setUploading(false);
      setUploadProgress(0);
    }
  }, [initializeMLSteps, simulateMLProcessing]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    multiple: true,
    // Accept all file types - backend will validate
    noClick: false,
    noKeyboard: false
  });

  const getSeverityColor = (severity: number) => {
    if (severity >= 0.8) return 'error';
    if (severity >= 0.6) return 'warning';
    if (severity >= 0.4) return 'info';
    return 'success';
  };

  const toggleAnomalyExpansion = (index: number) => {
    const newExpanded = new Set(expandedAnomalies);
    if (newExpanded.has(index)) {
      newExpanded.delete(index);
    } else {
      newExpanded.add(index);
    }
    setExpandedAnomalies(newExpanded);
  };

  const getMLInsightIcon = (type: string) => {
    switch (type) {
      case 'PORT_SCAN': return <NetworkCheck />;
      case 'SUSPICIOUS_IP': return <Warning />;
      case 'BRUTE_FORCE': return <Security />;
      case 'UNUSUAL_API': return <Assessment />;
      default: return <BugReport />;
    }
  };

  const getAnomalyTypeDescription = (type: string) => {
    const descriptions = {
      'PORT_SCAN': 'Systematic scanning of network ports indicating reconnaissance activity',
      'SUSPICIOUS_IP': 'IP address exhibiting unusual or potentially malicious behavior patterns',
      'BRUTE_FORCE': 'Repeated authentication attempts suggesting credential attacks',
      'UNUSUAL_API': 'Abnormal API usage patterns that deviate from baseline behavior'
    };
    return descriptions[type as keyof typeof descriptions] || 'Anomalous behavior detected by ML algorithms';
  };

  return (
    <Box sx={{ maxWidth: '100%', mx: 'auto', p: 2 }}>
      {/* Header Section with Animation */}
      <Fade in={true} timeout={1000}>
        <Box sx={{ mb: 4, textAlign: 'center' }}>
          <Box display="flex" alignItems="center" justifyContent="center" gap={2} mb={2}>
            <Avatar sx={{ bgcolor: '#1976d2', width: 56, height: 56 }}>
              <Psychology sx={{ fontSize: 32 }} />
            </Avatar>
            <Typography variant="h3" sx={{ fontWeight: 700, color: '#1976d2' }}>
              AI-Powered Log Analysis
            </Typography>
          </Box>
          <Typography variant="h6" color="text.secondary" sx={{ maxWidth: 800, mx: 'auto' }}>
            Advanced machine learning algorithms analyze your AWS logs to detect security anomalies, 
            behavioral patterns, and generate intelligent security rules
          </Typography>
        </Box>
      </Fade>

      {/* Upload Section */}
      <Slide in={true} direction="up" timeout={1200}>
        <Paper 
          elevation={3} 
          sx={{ 
            mb: 4, 
            overflow: 'hidden',
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            color: 'white'
          }}
        >
          <Box sx={{ p: 3 }}>
            <Box display="flex" alignItems="center" gap={2} mb={3}>
              <SmartToy sx={{ fontSize: 32 }} />
              <Typography variant="h5" sx={{ fontWeight: 600 }}>
                Intelligent Data Ingestion
              </Typography>
            </Box>

            <Box
              {...getRootProps()}
              sx={{
                border: '2px dashed rgba(255,255,255,0.5)',
                borderColor: isDragActive ? 'rgba(255,255,255,0.8)' : 'rgba(255,255,255,0.5)',
                borderRadius: 3,
                p: 4,
                textAlign: 'center',
                cursor: 'pointer',
                bgcolor: isDragActive ? 'rgba(255,255,255,0.1)' : 'transparent',
                transition: 'all 0.3s ease',
                '&:hover': {
                  bgcolor: 'rgba(255,255,255,0.1)',
                  borderColor: 'rgba(255,255,255,0.8)',
                  transform: 'translateY(-2px)',
                },
              }}
            >
              <input {...getInputProps()} />
              <Zoom in={true} timeout={1500}>
                <CloudUpload sx={{ fontSize: 64, mb: 2, opacity: 0.9 }} />
              </Zoom>
              <Typography variant="h5" sx={{ mb: 1, fontWeight: 600 }}>
                {isDragActive ? 'Release to Begin AI Analysis' : 'Upload AWS Log Files'}
              </Typography>
              <Typography variant="body1" sx={{ mb: 2, opacity: 0.9 }}>
                Drop your log files here or click to browse
              </Typography>
              <Box display="flex" justifyContent="center" gap={2} flexWrap="wrap">
                <Chip label="VPC Flow Logs" sx={{ bgcolor: 'rgba(255,255,255,0.2)', color: 'white' }} />
                <Chip label="CloudTrail" sx={{ bgcolor: 'rgba(255,255,255,0.2)', color: 'white' }} />
                <Chip label="WAF Logs" sx={{ bgcolor: 'rgba(255,255,255,0.2)', color: 'white' }} />
                <Chip label="ALB Logs" sx={{ bgcolor: 'rgba(255,255,255,0.2)', color: 'white' }} />
                <Chip label="IAM Logs" sx={{ bgcolor: 'rgba(255,255,255,0.2)', color: 'white' }} />
              </Box>
            </Box>
          </Box>
        </Paper>
      </Slide>

      {/* ML Processing Steps */}
      {uploading && (
        <Fade in={uploading} timeout={500}>
          <Paper elevation={2} sx={{ mb: 4, overflow: 'hidden' }}>
            <Box sx={{ 
              p: 3, 
              background: 'linear-gradient(90deg, #f093fb 0%, #f5576c 100%)',
              color: 'white'
            }}>
              <Box display="flex" alignItems="center" gap={2} mb={2}>
                <CircularProgress size={24} sx={{ color: 'white' }} />
                <Typography variant="h6" sx={{ fontWeight: 600 }}>
                  AI Processing Pipeline Active
                </Typography>
              </Box>
              <LinearProgress 
                variant="determinate" 
                value={uploadProgress} 
                sx={{ 
                  height: 8, 
                  borderRadius: 4,
                  bgcolor: 'rgba(255,255,255,0.3)',
                  '& .MuiLinearProgress-bar': {
                    bgcolor: 'white'
                  }
                }}
              />
            </Box>

            <Box sx={{ p: 3 }}>
              <Stepper activeStep={activeStep} orientation="vertical">
                {processingSteps.map((step, index) => (
                  <Step key={step.id}>
                    <StepLabel
                      StepIconComponent={() => (
                        <Avatar 
                          sx={{ 
                            bgcolor: step.completed ? '#4caf50' : step.active ? '#2196f3' : '#e0e0e0',
                            width: 40,
                            height: 40,
                            transition: 'all 0.3s ease'
                          }}
                        >
                          {step.completed ? <CheckCircle /> : step.active ? step.icon : step.icon}
                        </Avatar>
                      )}
                    >
                      <Typography variant="h6" sx={{ fontWeight: 600 }}>
                        {step.label}
                      </Typography>
                    </StepLabel>
                    <StepContent>
                      <Typography variant="body1" color="text.secondary">
                        {step.description}
                      </Typography>
                      {step.active && (
                        <Box display="flex" alignItems="center" gap={1} mt={1}>
                          <CircularProgress size={16} />
                          <Typography variant="body2" color="primary">
                            Processing...
                          </Typography>
                        </Box>
                      )}
                    </StepContent>
                  </Step>
                ))}
              </Stepper>
            </Box>
          </Paper>
        </Fade>
      )}

      {/* Error Display */}
      {error && (
        <Slide in={!!error} direction="up" timeout={500}>
          <Alert 
            severity="error" 
            sx={{ 
              mb: 4,
              '& .MuiAlert-message': { fontSize: '1rem' }
            }}
            icon={<Error />}
          >
            {error}
          </Alert>
        </Slide>
      )}

      {/* Results Section */}
      {uploadResult && (
        <Fade in={showMLInsights} timeout={1000}>
          <Box>
            {/* ML Analysis Summary */}
            <Paper elevation={3} sx={{ mb: 4, overflow: 'hidden' }}>
              <Box sx={{ 
                p: 3, 
                background: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)',
                color: 'white'
              }}>
                <Box display="flex" alignItems="center" gap={2} mb={2}>
                  <Analytics sx={{ fontSize: 32 }} />
                  <Typography variant="h5" sx={{ fontWeight: 600 }}>
                    ML Analysis Complete
                  </Typography>
                </Box>
                <Typography variant="body1" sx={{ opacity: 0.9 }}>
                  Advanced algorithms have processed your logs and identified behavioral patterns
                </Typography>
              </Box>

              <Box sx={{ p: 3 }}>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={3}>
                    <Zoom in={showMLInsights} timeout={1000}>
                      <Paper elevation={1} sx={{ p: 3, textAlign: 'center', bgcolor: '#f8f9fa' }}>
                        <Avatar sx={{ bgcolor: '#2196f3', mx: 'auto', mb: 2, width: 56, height: 56 }}>
                          <DataUsage sx={{ fontSize: 28 }} />
                        </Avatar>
                        <Typography variant="h4" sx={{ fontWeight: 700, color: '#2196f3' }}>
                          {uploadResult.logs_processed.toLocaleString()}
                        </Typography>
                        <Typography variant="body1" color="text.secondary">
                          Log Entries Analyzed
                        </Typography>
                      </Paper>
                    </Zoom>
                  </Grid>

                  <Grid item xs={12} md={3}>
                    <Zoom in={showMLInsights} timeout={1200}>
                      <Paper elevation={1} sx={{ p: 3, textAlign: 'center', bgcolor: '#fff3e0' }}>
                        <Avatar sx={{ bgcolor: '#ff9800', mx: 'auto', mb: 2, width: 56, height: 56 }}>
                          <TrendingUp sx={{ fontSize: 28 }} />
                        </Avatar>
                        <Typography variant="h4" sx={{ fontWeight: 700, color: '#ff9800' }}>
                          {uploadResult.anomalies_detected}
                        </Typography>
                        <Typography variant="body1" color="text.secondary">
                          Anomalies Detected
                        </Typography>
                      </Paper>
                    </Zoom>
                  </Grid>

                  <Grid item xs={12} md={3}>
                    <Zoom in={showMLInsights} timeout={1400}>
                      <Paper elevation={1} sx={{ p: 3, textAlign: 'center', bgcolor: '#e8f5e8' }}>
                        <Avatar sx={{ bgcolor: '#4caf50', mx: 'auto', mb: 2, width: 56, height: 56 }}>
                          <AutoFixHigh sx={{ fontSize: 28 }} />
                        </Avatar>
                        <Typography variant="h4" sx={{ fontWeight: 700, color: '#4caf50' }}>
                          {uploadResult.candidate_rules_generated}
                        </Typography>
                        <Typography variant="body1" color="text.secondary">
                          Security Rules Generated
                        </Typography>
                      </Paper>
                    </Zoom>
                  </Grid>

                  <Grid item xs={12} md={3}>
                    <Zoom in={showMLInsights} timeout={1600}>
                      <Paper elevation={1} sx={{ p: 3, textAlign: 'center', bgcolor: '#f3e5f5' }}>
                        <Avatar sx={{ bgcolor: '#9c27b0', mx: 'auto', mb: 2, width: 56, height: 56 }}>
                          <Speed sx={{ fontSize: 28 }} />
                        </Avatar>
                        <Typography variant="h4" sx={{ fontWeight: 700, color: '#9c27b0' }}>
                          99.7%
                        </Typography>
                        <Typography variant="body1" color="text.secondary">
                          ML Accuracy Score
                        </Typography>
                      </Paper>
                    </Zoom>
                  </Grid>
                </Grid>
              </Box>
            </Paper>

            {/* Anomaly Results */}
            {uploadResult.anomalies.length > 0 ? (
              <Paper elevation={2} sx={{ mb: 4 }}>
                <Box sx={{ p: 3, borderBottom: '1px solid #e0e0e0' }}>
                  <Box display="flex" alignItems="center" gap={2}>
                    <Badge badgeContent={uploadResult.anomalies.length} color="error">
                      <Shield sx={{ fontSize: 32, color: '#f44336' }} />
                    </Badge>
                    <Typography variant="h5" sx={{ fontWeight: 600 }}>
                      Security Anomalies Detected
                    </Typography>
                  </Box>
                  <Typography variant="body1" color="text.secondary" sx={{ mt: 1 }}>
                    Our ML algorithms have identified the following security concerns in your logs
                  </Typography>
                </Box>

                <List sx={{ p: 0 }}>
                  {uploadResult.anomalies.map((anomaly, index) => {
                    const isExpanded = expandedAnomalies.has(index);

                    return (
                      <Box key={index}>
                        <ListItem
                          sx={{
                            cursor: 'pointer',
                            '&:hover': { bgcolor: '#f5f5f5' },
                            transition: 'background-color 0.2s'
                          }}
                          onClick={() => toggleAnomalyExpansion(index)}
                        >
                          <ListItemAvatar>
                            <Avatar sx={{ 
                              bgcolor: getSeverityColor(anomaly.severity) === 'error' ? '#f44336' : 
                                       getSeverityColor(anomaly.severity) === 'warning' ? '#ff9800' : '#2196f3'
                            }}>
                              {getMLInsightIcon(anomaly.type)}
                            </Avatar>
                          </ListItemAvatar>
                          <ListItemText
                            primary={
                              <Box display="flex" alignItems="center" gap={2}>
                                <Typography variant="h6" sx={{ fontWeight: 600 }}>
                                  {anomaly.type.replace('_', ' ')} Detected
                                </Typography>
                                <Chip
                                  label={`${Math.round(anomaly.severity * 100)}% Severity`}
                                  color={getSeverityColor(anomaly.severity)}
                                  size="small"
                                />
                                <Chip
                                  label={`${Math.round(anomaly.confidence * 100)}% Confidence`}
                                  variant="outlined"
                                  size="small"
                                />
                              </Box>
                            }
                            secondary={getAnomalyTypeDescription(anomaly.type)}
                          />
                          <IconButton>
                            {isExpanded ? <ExpandLess /> : <ExpandMore />}
                          </IconButton>
                        </ListItem>

                        <Collapse in={isExpanded} timeout="auto" unmountOnExit>
                          <Box sx={{ p: 3, bgcolor: '#fafafa' }}>
                            <Grid container spacing={3}>
                              <Grid item xs={12} md={6}>
                                <Typography variant="subtitle1" sx={{ fontWeight: 600, mb: 2 }}>
                                  ðŸŽ¯ Affected Resources
                                </Typography>
                                <Box display="flex" flexWrap="wrap" gap={1} mb={3}>
                                  {anomaly.affected_resources.map((resource: string, idx: number) => (
                                    <Chip
                                      key={idx}
                                      label={resource}
                                      variant="outlined"
                                      size="small"
                                      sx={{ bgcolor: 'white' }}
                                    />
                                  ))}
                                </Box>

                                <Typography variant="subtitle1" sx={{ fontWeight: 600, mb: 2 }}>
                                  â±ï¸ Detection Timeline
                                </Typography>
                                <Paper sx={{ p: 2, bgcolor: 'white' }}>
                                  <Typography variant="body2">
                                    <strong>Start:</strong> {new Date(anomaly.time_window.start).toLocaleString()}
                                  </Typography>
                                  <Typography variant="body2">
                                    <strong>End:</strong> {new Date(anomaly.time_window.end).toLocaleString()}
                                  </Typography>
                                  <Typography variant="body2">
                                    <strong>Duration:</strong> {Math.round((new Date(anomaly.time_window.end).getTime() - new Date(anomaly.time_window.start).getTime()) / 60000)} minutes
                                  </Typography>
                                </Paper>
                              </Grid>

                              <Grid item xs={12} md={6}>
                                <Typography variant="subtitle1" sx={{ fontWeight: 600, mb: 2 }}>
                                  ðŸ”¬ ML Analysis Patterns
                                </Typography>
                                {(anomaly as any).patterns && (anomaly as any).patterns.length > 0 ? (
                                  <Box>
                                    {(anomaly as any).patterns.map((pattern: any, idx: number) => (
                                      <Paper key={idx} sx={{ p: 2, mb: 1, bgcolor: 'white' }}>
                                        <Typography variant="body2" sx={{ fontWeight: 600 }}>
                                          {pattern.feature}
                                        </Typography>
                                        <Typography variant="body2" color="text.secondary">
                                          Expected: {pattern.expected_range[0]}-{pattern.expected_range[1]}
                                        </Typography>
                                        <Typography variant="body2" color="error.main">
                                          Found: {pattern.actual_value} ({Math.round(pattern.deviation * 100)}% deviation)
                                        </Typography>
                                      </Paper>
                                    ))}
                                  </Box>
                                ) : (
                                  <Paper sx={{ p: 2, bgcolor: 'white' }}>
                                    <Typography variant="body2" color="text.secondary">
                                      Statistical anomaly detected through behavioral analysis
                                    </Typography>
                                  </Paper>
                                )}

                                <Typography variant="subtitle1" sx={{ fontWeight: 600, mt: 3, mb: 2 }}>
                                  ðŸ›¡ï¸ Recommended Actions
                                </Typography>
                                <Paper sx={{ p: 2, bgcolor: '#e3f2fd' }}>
                                  {anomaly.type === 'PORT_SCAN' && (
                                    <Box>
                                      <Typography variant="body2">â€¢ Block suspicious IP addresses</Typography>
                                      <Typography variant="body2">â€¢ Enable intrusion detection</Typography>
                                      <Typography variant="body2">â€¢ Review firewall configurations</Typography>
                                    </Box>
                                  )}
                                  {anomaly.type === 'SUSPICIOUS_IP' && (
                                    <Box>
                                      <Typography variant="body2">â€¢ Investigate IP reputation</Typography>
                                      <Typography variant="body2">â€¢ Consider IP blocking</Typography>
                                      <Typography variant="body2">â€¢ Monitor future activity</Typography>
                                    </Box>
                                  )}
                                  {anomaly.type === 'BRUTE_FORCE' && (
                                    <Box>
                                      <Typography variant="body2">â€¢ Implement account lockouts</Typography>
                                      <Typography variant="body2">â€¢ Enable MFA</Typography>
                                      <Typography variant="body2">â€¢ Monitor authentication logs</Typography>
                                    </Box>
                                  )}
                                  {anomaly.type === 'UNUSUAL_API' && (
                                    <Box>
                                      <Typography variant="body2">â€¢ Review API access patterns</Typography>
                                      <Typography variant="body2">â€¢ Validate API credentials</Typography>
                                      <Typography variant="body2">â€¢ Implement rate limiting</Typography>
                                    </Box>
                                  )}
                                </Paper>
                              </Grid>
                            </Grid>
                          </Box>
                        </Collapse>

                        {index < uploadResult.anomalies.length - 1 && <Divider />}
                      </Box>
                    );
                  })}
                </List>
              </Paper>
            ) : (
              <Zoom in={showMLInsights} timeout={1000}>
                <Paper elevation={2} sx={{ p: 4, textAlign: 'center', bgcolor: '#e8f5e8' }}>
                  <Avatar sx={{ bgcolor: '#4caf50', mx: 'auto', mb: 2, width: 80, height: 80 }}>
                    <Shield sx={{ fontSize: 40 }} />
                  </Avatar>
                  <Typography variant="h5" sx={{ fontWeight: 600, color: '#4caf50', mb: 2 }}>
                    All Clear! No Anomalies Detected
                  </Typography>
                  <Typography variant="body1" color="text.secondary">
                    Our advanced ML algorithms have analyzed {uploadResult.logs_processed.toLocaleString()} log entries 
                    and found no suspicious activity. Your infrastructure appears to be secure.
                  </Typography>
                </Paper>
              </Zoom>
            )}
          </Box>
        </Fade>
      )}
    </Box>
  );
};

export default LogUpload;
