import React, { useState } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  Box,
  Button,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Chip,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  Collapse,
  Alert,
} from '@mui/material';
import {
  CloudUpload,
  Scanner as ScannerIcon,
  Folder,
  Description,
  Error,
  Warning,
  Info,
  CheckCircle,
  ExpandMore,
  ExpandLess,
  Download,
} from '@mui/icons-material';
import { useDropzone } from 'react-dropzone';

interface ScanResult {
  id: string;
  severity: 'critical' | 'high' | 'medium' | 'low';
  rule_id: string;
  description: string;
  file_path: string;
  line_number: number;
  remediation: string;
}

const Scanner: React.FC = () => {
  const [scanType, setScanType] = useState('file');
  const [scanResults, setScanResults] = useState<ScanResult[]>([]);
  const [isScanning, setIsScanning] = useState(false);
  const [expandedRows, setExpandedRows] = useState<Set<string>>(new Set());

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: {
      'text/plain': ['.tf', '.json'],
    },
    onDrop: (acceptedFiles) => {
      console.log('Files dropped:', acceptedFiles);
      // Handle file upload logic here
    },
  });

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case 'critical': return <Error sx={{ color: '#ef4444' }} />;
      case 'high': return <Warning sx={{ color: '#f59e0b' }} />;
      case 'medium': return <Info sx={{ color: '#06b6d4' }} />;
      case 'low': return <CheckCircle sx={{ color: '#10b981' }} />;
      default: return <Info />;
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical': return '#ef4444';
      case 'high': return '#f59e0b';
      case 'medium': return '#06b6d4';
      case 'low': return '#10b981';
      default: return '#6b7280';
    }
  };

  const handleRowExpand = (id: string) => {
    const newExpanded = new Set(expandedRows);
    if (newExpanded.has(id)) {
      newExpanded.delete(id);
    } else {
      newExpanded.add(id);
    }
    setExpandedRows(newExpanded);
  };

  const mockScanResults: ScanResult[] = [
    {
      id: '1',
      severity: 'critical',
      rule_id: 'sg-001',
      description: 'Security group allows SSH (port 22) from 0.0.0.0/0',
      file_path: 'terraform/security.tf',
      line_number: 15,
      remediation: 'Restrict SSH access to specific IP ranges or use bastion hosts',
    },
    {
      id: '2',
      severity: 'high',
      rule_id: 's3-001',
      description: 'S3 bucket has public-read ACL which allows public access',
      file_path: 'terraform/storage.tf',
      line_number: 8,
      remediation: 'Remove public ACL and use bucket policies for controlled access',
    },
    {
      id: '3',
      severity: 'medium',
      rule_id: 'rds-003',
      description: 'RDS instance has automated backups disabled',
      file_path: 'terraform/database.tf',
      line_number: 23,
      remediation: 'Set backup retention period to at least 7 days',
    },
  ];

  const handleScan = () => {
    setIsScanning(true);
    // Simulate scanning
    setTimeout(() => {
      setScanResults(mockScanResults);
      setIsScanning(false);
    }, 2000);
  };

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      {/* Header */}
      <Box sx={{ mb: 6, textAlign: 'center' }}>
        <Typography variant="h1" sx={{ mb: 2 }}>
          Infrastructure Scanner
        </Typography>
        <Typography variant="body1" sx={{ fontSize: '1.2rem', mb: 4, maxWidth: 600, mx: 'auto' }}>
          Scan your Terraform files and infrastructure configurations for security vulnerabilities
        </Typography>
      </Box>

      <Grid container spacing={4}>
        {/* Scan Configuration */}
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent sx={{ p: 3 }}>
              <Typography variant="h4" sx={{ mb: 3 }}>
                Scan Configuration
              </Typography>

              {/* Scan Type */}
              <FormControl fullWidth sx={{ mb: 3 }}>
                <InputLabel>Scan Type</InputLabel>
                <Select
                  value={scanType}
                  label="Scan Type"
                  onChange={(e) => setScanType(e.target.value)}
                >
                  <MenuItem value="file">Single File</MenuItem>
                  <MenuItem value="directory">Directory</MenuItem>
                  <MenuItem value="upload">File Upload</MenuItem>
                </Select>
              </FormControl>

              {/* File Upload Area */}
              {scanType === 'upload' && (
                <Box
                  {...getRootProps()}
                  sx={{
                    border: '2px dashed rgba(99, 102, 241, 0.3)',
                    borderRadius: '12px',
                    p: 3,
                    textAlign: 'center',
                    cursor: 'pointer',
                    mb: 3,
                    backgroundColor: isDragActive ? 'rgba(99, 102, 241, 0.1)' : 'transparent',
                    '&:hover': {
                      backgroundColor: 'rgba(99, 102, 241, 0.05)',
                    },
                  }}
                >
                  <input {...getInputProps()} />
                  <CloudUpload sx={{ fontSize: 48, color: '#6366f1', mb: 2 }} />
                  <Typography variant="body1" sx={{ mb: 1 }}>
                    {isDragActive ? 'Drop files here' : 'Drag & drop files here'}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    or click to select files (.tf, .json)
                  </Typography>
                </Box>
              )}

              {/* File Path Input */}
              {scanType !== 'upload' && (
                <TextField
                  fullWidth
                  label={scanType === 'file' ? 'File Path' : 'Directory Path'}
                  placeholder={scanType === 'file' ? 'terraform/main.tf' : 'terraform/'}
                  sx={{ mb: 3 }}
                />
              )}

              {/* Severity Filter */}
              <FormControl fullWidth sx={{ mb: 3 }}>
                <InputLabel>Minimum Severity</InputLabel>
                <Select
                  defaultValue="low"
                  label="Minimum Severity"
                >
                  <MenuItem value="low">Low</MenuItem>
                  <MenuItem value="medium">Medium</MenuItem>
                  <MenuItem value="high">High</MenuItem>
                  <MenuItem value="critical">Critical</MenuItem>
                </Select>
              </FormControl>

              {/* Scan Button */}
              <Button
                fullWidth
                variant="contained"
                size="large"
                startIcon={<ScannerIcon />}
                onClick={handleScan}
                disabled={isScanning}
                sx={{ mb: 2 }}
              >
                {isScanning ? 'Scanning...' : 'Start Scan'}
              </Button>

              {scanResults.length > 0 && (
                <Button
                  fullWidth
                  variant="outlined"
                  startIcon={<Download />}
                  sx={{
                    borderColor: 'rgba(255, 255, 255, 0.2)',
                    color: '#cbd5e1',
                    '&:hover': {
                      borderColor: '#6366f1',
                      backgroundColor: 'rgba(99, 102, 241, 0.1)',
                    },
                  }}
                >
                  Export Results
                </Button>
              )}
            </CardContent>
          </Card>
        </Grid>

        {/* Scan Results */}
        <Grid item xs={12} md={8}>
          <Card>
            <CardContent sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 3 }}>
                <Typography variant="h4">Scan Results</Typography>
                {scanResults.length > 0 && (
                  <Box sx={{ display: 'flex', gap: 1 }}>
                    <Chip
                      label={`${scanResults.filter(r => r.severity === 'critical').length} Critical`}
                      size="small"
                      sx={{ backgroundColor: 'rgba(239, 68, 68, 0.1)', color: '#ef4444' }}
                    />
                    <Chip
                      label={`${scanResults.filter(r => r.severity === 'high').length} High`}
                      size="small"
                      sx={{ backgroundColor: 'rgba(245, 158, 11, 0.1)', color: '#f59e0b' }}
                    />
                    <Chip
                      label={`${scanResults.filter(r => r.severity === 'medium').length} Medium`}
                      size="small"
                      sx={{ backgroundColor: 'rgba(6, 182, 212, 0.1)', color: '#06b6d4' }}
                    />
                    <Chip
                      label={`${scanResults.filter(r => r.severity === 'low').length} Low`}
                      size="small"
                      sx={{ backgroundColor: 'rgba(16, 185, 129, 0.1)', color: '#10b981' }}
                    />
                  </Box>
                )}
              </Box>

              {scanResults.length === 0 ? (
                <Box sx={{ textAlign: 'center', py: 6 }}>
                  <ScannerIcon sx={{ fontSize: 64, color: '#6b7280', mb: 2 }} />
                  <Typography variant="h6" color="text.secondary" sx={{ mb: 1 }}>
                    No scan results yet
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Configure and run a scan to see security issues
                  </Typography>
                </Box>
              ) : (
                <TableContainer component={Paper} sx={{ backgroundColor: 'transparent' }}>
                  <Table>
                    <TableHead>
                      <TableRow>
                        <TableCell>Severity</TableCell>
                        <TableCell>Issue</TableCell>
                        <TableCell>File</TableCell>
                        <TableCell>Line</TableCell>
                        <TableCell>Actions</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {scanResults.map((result) => (
                        <React.Fragment key={result.id}>
                          <TableRow
                            sx={{
                              '&:hover': {
                                backgroundColor: 'rgba(255, 255, 255, 0.02)',
                              },
                            }}
                          >
                            <TableCell>
                              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                                {getSeverityIcon(result.severity)}
                                <Chip
                                  label={result.severity.toUpperCase()}
                                  size="small"
                                  sx={{
                                    backgroundColor: `${getSeverityColor(result.severity)}20`,
                                    color: getSeverityColor(result.severity),
                                    border: 'none',
                                    fontWeight: 600,
                                  }}
                                />
                              </Box>
                            </TableCell>
                            <TableCell>
                              <Typography variant="body2" sx={{ fontWeight: 500 }}>
                                {result.description}
                              </Typography>
                              <Typography variant="caption" color="text.secondary">
                                Rule: {result.rule_id}
                              </Typography>
                            </TableCell>
                            <TableCell>
                              <Typography variant="body2" sx={{ fontFamily: 'monospace' }}>
                                {result.file_path}
                              </Typography>
                            </TableCell>
                            <TableCell>
                              <Typography variant="body2" sx={{ fontFamily: 'monospace' }}>
                                {result.line_number}
                              </Typography>
                            </TableCell>
                            <TableCell>
                              <IconButton
                                size="small"
                                onClick={() => handleRowExpand(result.id)}
                              >
                                {expandedRows.has(result.id) ? <ExpandLess /> : <ExpandMore />}
                              </IconButton>
                            </TableCell>
                          </TableRow>
                          <TableRow>
                            <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={5}>
                              <Collapse in={expandedRows.has(result.id)} timeout="auto" unmountOnExit>
                                <Box sx={{ py: 2 }}>
                                  <Alert
                                    severity="info"
                                    sx={{
                                      backgroundColor: 'rgba(6, 182, 212, 0.1)',
                                      border: '1px solid rgba(6, 182, 212, 0.2)',
                                      '& .MuiAlert-icon': {
                                        color: '#06b6d4',
                                      },
                                    }}
                                  >
                                    <Typography variant="body2" sx={{ fontWeight: 600, mb: 1 }}>
                                      Remediation:
                                    </Typography>
                                    <Typography variant="body2">
                                      {result.remediation}
                                    </Typography>
                                  </Alert>
                                </Box>
                              </Collapse>
                            </TableCell>
                          </TableRow>
                        </React.Fragment>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              )}
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Scanner;
