import React, { useState, useCallback } from 'react';
import {
  Box,
  Button,
  Typography,
  TextField,
  Alert,
  Chip,
  Tabs,
  Tab,
  Grid,
  LinearProgress,
  Divider,
  Paper,
  IconButton,
  Collapse
} from '@mui/material';
import {
  CloudUpload,
  Code,
  Security,
  Warning,
  Info,
  CheckCircle,
  BugReport,
  Shield,
  Assessment,
  ExpandLess,
  ExpandMore as ExpandMoreIcon
} from '@mui/icons-material';
import { useDropzone } from 'react-dropzone';
import apiClient from '../config/api';
import { ScanResult, Severity } from '../types';

interface ScanResponse {
  message: string;
  files_scanned?: number;
  issues_found: number;
  results: ScanResult[];
}

interface SecurityInsight {
  category: string;
  count: number;
  severity: Severity;
  description: string;
}

const IaCScanner: React.FC = () => {
  const [tabValue, setTabValue] = useState(0);
  const [terraformCode, setTerraformCode] = useState('');
  const [scanning, setScanning] = useState(false);
  const [scanResult, setScanResult] = useState<ScanResponse | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [expandedResults, setExpandedResults] = useState<Set<number>>(new Set());

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    if (acceptedFiles.length === 0) return;

    setScanning(true);
    setError(null);
    setScanResult(null);

    try {
      const formData = new FormData();
      acceptedFiles.forEach(file => {
        formData.append('files', file);
      });

      const response = await apiClient.post('/api/iac/scan', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setScanResult(response.data);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to scan files');
    } finally {
      setScanning(false);
    }
  }, []);

  const handleTextScan = async () => {
    if (!terraformCode.trim()) {
      setError('Please enter Terraform code to scan');
      return;
    }

    setScanning(true);
    setError(null);
    setScanResult(null);

    try {
      const formData = new FormData();
      formData.append('terraform_code', terraformCode);

      const response = await apiClient.post('/api/iac/scan-text', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setScanResult(response.data);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to scan code');
    } finally {
      setScanning(false);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'text/plain': ['.tf', '.hcl'],
      'application/octet-stream': ['.tf', '.hcl']
    },
    multiple: true
  });

  const getSeverityIcon = (severity: Severity) => {
    switch (severity) {
      case Severity.CRITICAL:
        return <BugReport sx={{ color: '#d32f2f' }} />;
      case Severity.HIGH:
        return <Warning sx={{ color: '#f57c00' }} />;
      case Severity.MEDIUM:
        return <Info sx={{ color: '#1976d2' }} />;
      case Severity.LOW:
        return <Info sx={{ color: '#616161' }} />;
      default:
        return <Info sx={{ color: '#616161' }} />;
    }
  };

  const getSeverityColor = (severity: Severity): string => {
    switch (severity) {
      case Severity.CRITICAL:
        return '#d32f2f';
      case Severity.HIGH:
        return '#f57c00';
      case Severity.MEDIUM:
        return '#1976d2';
      case Severity.LOW:
        return '#616161';
      default:
        return '#616161';
    }
  };

  const getSeverityBackground = (severity: Severity): string => {
    switch (severity) {
      case Severity.CRITICAL:
        return '#ffebee';
      case Severity.HIGH:
        return '#fff3e0';
      case Severity.MEDIUM:
        return '#e3f2fd';
      case Severity.LOW:
        return '#f5f5f5';
      default:
        return '#f5f5f5';
    }
  };

  const groupResultsBySeverity = (results: ScanResult[]) => {
    const grouped = results.reduce((acc, result) => {
      if (!acc[result.severity]) {
        acc[result.severity] = [];
      }
      acc[result.severity].push(result);
      return acc;
    }, {} as Record<Severity, ScanResult[]>);

    // Sort by severity priority
    const severityOrder = [Severity.CRITICAL, Severity.HIGH, Severity.MEDIUM, Severity.LOW];
    return severityOrder.reduce((acc, severity) => {
      if (grouped[severity]) {
        acc[severity] = grouped[severity];
      }
      return acc;
    }, {} as Record<Severity, ScanResult[]>);
  };

  const getSecurityInsights = (results: ScanResult[]): SecurityInsight[] => {
    const insights: SecurityInsight[] = [];
    const grouped = groupResultsBySeverity(results);

    Object.entries(grouped).forEach(([severity, severityResults]) => {
      const categories = severityResults.reduce((acc, result) => {
        const category = result.rule_id.split('-')[0].toUpperCase();
        acc[category] = (acc[category] || 0) + 1;
        return acc;
      }, {} as Record<string, number>);

      Object.entries(categories).forEach(([category, count]) => {
        insights.push({
          category,
          count,
          severity: severity as Severity,
          description: getCategoryDescription(category)
        });
      });
    });

    return insights.sort((a, b) => {
      const severityWeight = { CRITICAL: 4, HIGH: 3, MEDIUM: 2, LOW: 1 };
      return (severityWeight[b.severity] || 0) - (severityWeight[a.severity] || 0);
    });
  };

  const getCategoryDescription = (category: string): string => {
    const descriptions: Record<string, string> = {
      'S3': 'Amazon S3 storage security configurations',
      'SG': 'Security Group network access controls',
      'EC2': 'Elastic Compute Cloud instance security',
      'RDS': 'Relational Database Service security',
      'IAM': 'Identity and Access Management policies',
      'VPC': 'Virtual Private Cloud network security',
      'KMS': 'Key Management Service encryption',
      'LAMBDA': 'AWS Lambda function security',
      'CLOUDTRAIL': 'CloudTrail logging and monitoring'
    };
    return descriptions[category] || `${category} security configurations`;
  };

  const toggleResultExpansion = (index: number) => {
    const newExpanded = new Set(expandedResults);
    if (newExpanded.has(index)) {
      newExpanded.delete(index);
    } else {
      newExpanded.add(index);
    }
    setExpandedResults(newExpanded);
  };

  return (
    <Box sx={{ minHeight: '100vh', p: 4 }}>
      {/* Art Deco Header Section */}
      <Box sx={{ textAlign: 'center', mb: 8, position: 'relative' }}>
        {/* Decorative Top Line */}
        <Box sx={{ 
          position: 'absolute',
          top: 0,
          left: '50%',
