sourcesContent:
import React, { useState, useCallback } from 'react';
import {
  Box,
  Button,
  Typography,
  TextField,
  Alert,
  Chip,
  Tabs,
  Tab,
  Grid,
  Paper,
  IconButton,
  Collapse,
  Card
} from '@mui/material';
import {
  CloudUpload,
  Code,
  Security,
  Warning,
  Info,
  CheckCircle,
  BugReport,
  Shield,
  Assessment,
  ExpandLess,
  ExpandMore as ExpandMoreIcon
} from '@mui/icons-material';
import { useDropzone } from 'react-dropzone';
import apiClient from '../config/api';
import { ScanResult, Severity } from '../types';

interface ScanResponse {
  message: string;
  files_scanned?: number;
  issues_found: number;
  results: ScanResult[];
}

interface SecurityInsight {
  category: string;
  count: number;
  severity: Severity;
  description: string;
}

const IaCScanner: React.FC = () => {
  const [tabValue, setTabValue] = useState(0);
  const [terraformCode, setTerraformCode] = useState('');
  const [scanning, setScanning] = useState(false);
  const [scanResult, setScanResult] = useState<ScanResponse | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [expandedResults, setExpandedResults] = useState<Set<number>>(new Set());

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    if (acceptedFiles.length === 0) return;

    setScanning(true);
    setError(null);
    setScanResult(null);

    try {
      const formData = new FormData();
      acceptedFiles.forEach(file => {
        formData.append('files', file);
      });

      const response = await apiClient.post('/api/iac/scan', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setScanResult(response.data);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to scan files');
    } finally {
      setScanning(false);
    }
  }, []);

  const handleTextScan = async () => {
    if (!terraformCode.trim()) {
      setError('Please enter Terraform code to scan');
      return;
    }

    setScanning(true);
    setError(null);
    setScanResult(null);

    try {
      const formData = new FormData();
      formData.append('terraform_code', terraformCode);

      const response = await apiClient.post('/api/iac/scan-text', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setScanResult(response.data);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to scan code');
    } finally {
      setScanning(false);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'text/plain': ['.tf', '.hcl'],
      'application/octet-stream': ['.tf', '.hcl']
    },
    multiple: true
  });

  const getSeverityIcon = (severity: Severity) => {
    switch (severity) {
      case Severity.CRITICAL:
        return <BugReport sx={{ color: '#d32f2f' }} />;
      case Severity.HIGH:
        return <Warning sx={{ color: '#f57c00' }} />;
      case Severity.MEDIUM:
        return <Info sx={{ color: '#1976d2' }} />;
      case Severity.LOW:
        return <Info sx={{ color: '#616161' }} />;
      default:
        return <Info sx={{ color: '#616161' }} />;
    }
  };

  const getSeverityColor = (severity: Severity): string => {
    switch (severity) {
      case Severity.CRITICAL:
        return '#d32f2f';
      case Severity.HIGH:
        return '#f57c00';
      case Severity.MEDIUM:
        return '#1976d2';
      case Severity.LOW:
        return '#616161';
      default:
        return '#616161';
    }
  };

  const getSeverityBackground = (severity: Severity): string => {
    switch (severity) {
      case Severity.CRITICAL:
        return '#ffebee';
      case Severity.HIGH:
        return '#fff3e0';
      case Severity.MEDIUM:
        return '#e3f2fd';
      case Severity.LOW:
        return '#f5f5f5';
      default:
        return '#f5f5f5';
    }
  };

  const groupResultsBySeverity = (results: ScanResult[]) => {
    const grouped = results.reduce((acc, result) => {
      if (!acc[result.severity]) {
        acc[result.severity] = [];
      }
      acc[result.severity].push(result);
      return acc;
    }, {} as Record<Severity, ScanResult[]>);

    const severityOrder = [Severity.CRITICAL, Severity.HIGH, Severity.MEDIUM, Severity.LOW];
    return severityOrder.reduce((acc, severity) => {
      if (grouped[severity]) {
        acc[severity] = grouped[severity];
      }
      return acc;
    }, {} as Record<Severity, ScanResult[]>);
  };

  const getSecurityInsights = (results: ScanResult[]): SecurityInsight[] => {
    const insights: SecurityInsight[] = [];
    const grouped = groupResultsBySeverity(results);

    Object.entries(grouped).forEach(([severity, severityResults]) => {
      const categories = severityResults.reduce((acc, result) => {
        const category = result.rule_id.split('-')[0].toUpperCase();
        acc[category] = (acc[category] || 0) + 1;
        return acc;
      }, {} as Record<string, number>);

      Object.entries(categories).forEach(([category, count]) => {
        insights.push({
          category,
          count,
          severity: severity as Severity,
          description: getCategoryDescription(category)
        });
      });
    });

    return insights.sort((a, b) => {
      const severityWeight = { CRITICAL: 4, HIGH: 3, MEDIUM: 2, LOW: 1 };
      return (severityWeight[b.severity] || 0) - (severityWeight[a.severity] || 0);
    });
  };

  const getCategoryDescription = (category: string): string => {
    const descriptions: Record<string, string> = {
      'S3': 'Amazon S3 storage security configurations',
      'SG': 'Security Group network access controls',
      'EC2': 'Elastic Compute Cloud instance security',
      'RDS': 'Relational Database Service security',
      'IAM': 'Identity and Access Management policies',
      'VPC': 'Virtual Private Cloud network security',
      'KMS': 'Key Management Service encryption',
      'LAMBDA': 'AWS Lambda function security',
      'CLOUDTRAIL': 'CloudTrail logging and monitoring'
    };
    return descriptions[category] || `${category} security configurations`;
  };

  const toggleResultExpansion = (index: number) => {
    const newExpanded = new Set(expandedResults);
    if (newExpanded.has(index)) {
      newExpanded.delete(index);
    } else {
      newExpanded.add(index);
    }
    setExpandedResults(newExpanded);
  };

  return (
    <Box sx={{ minHeight: '100vh', p: 4 }}>
      {/* Hand-Drawn Header Section */}
      <Box sx={{ textAlign: 'center', mb: 6, position: 'relative' }}>
        {/* Decorative doodles */}
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            left: 20,
            fontSize: '2rem',
            transform: 'rotate(-20deg)',
            opacity: 0.6,
          }}
        >
          ðŸ”
        </Box>
        <Box
          sx={{
            position: 'absolute',
            top: 10,
            right: 30,
            fontSize: '1.5rem',
            transform: 'rotate(25deg)',
            opacity: 0.5,
          }}
        >
          ðŸ“‹
        </Box>

        {/* Wobbly Icon */}
        <Box
          sx={{
            width: 100,
            height: 100,
            backgroundColor: '#2d5da1',
            borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
            border: '4px solid #2d2d2d',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            mx: 'auto',
            mb: 4,
            transform: 'rotate(-2deg)',
            boxShadow: '6px 6px 0px 0px #2d2d2d',
            position: 'relative',
            '&::before': {
              content: '"âš¡"',
              position: 'absolute',
              top: -15,
              right: -15,
              fontSize: '1.8rem',
              transform: 'rotate(30deg)',
            }
          }}
        >
          <Security 
            sx={{ 
              fontSize: 40, 
              color: '#ffffff',
            }} 
          />
        </Box>

        <Typography 
          variant="h2" 
          sx={{ 
            color: '#2d2d2d',
            mb: 2,
            transform: 'rotate(0.5deg)',
            position: 'relative',
            '&::after': {
              content: '"ðŸ›¡ï¸"',
              marginLeft: '0.3em',
              transform: 'rotate(-10deg)',
              display: 'inline-block',
            }
          }}
        >
          Code Scanner
        </Typography>
        <Typography 
          variant="h5" 
          sx={{ 
            color: '#2d2d2d',
            fontFamily: '"Patrick Hand", cursive',
            mb: 3,
            transform: 'rotate(-0.3deg)',
            position: 'relative',
            '&::before': {
              content: '""',
              position: 'absolute',
              bottom: -3,
              left: '10%',
              right: '10%',
              height: 3,
              backgroundColor: '#fff9c4',
              zIndex: -1,
              transform: 'rotate(1deg)',
            }
          }}
        >
          Terraform Security Check
        </Typography>
        <Typography 
          variant="body1" 
          sx={{ 
            color: '#2d2d2d',
            maxWidth: 600,
            mx: 'auto',
            lineHeight: 1.8,
            fontSize: '1.2rem',
          }}
        >
          Upload your Terraform files or paste code below. We'll check it against 150+ security rules 
          and tell you what needs fixing! ðŸš€
        </Typography>
      </Box>

      {/* Hand-Drawn Input Section */}
      <Box 
        sx={{ 
          mb: 6, 
          backgroundColor: '#ffffff',
          borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
          border: '3px solid #2d2d2d',
          boxShadow: '4px 4px 0px 0px #2d2d2d',
          transform: 'rotate(-0.5deg)',
          position: 'relative',
          // Tape decoration
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 20,
            left: '50%',
            transform: 'translateX(-50%) rotate(-8deg)',
            width: 80,
            height: 25,
            backgroundColor: 'rgba(255, 255, 196, 0.8)',
            border: '1px solid rgba(45, 45, 45, 0.2)',
            zIndex: 1,
          }
        }}
      >
        {/* Hand-Drawn Tabs */}
        <Box sx={{ borderBottom: '2px dashed #2d2d2d', p: 2 }}>
          <Tabs 
            value={tabValue} 
            onChange={(_, newValue) => setTabValue(newValue)}
            sx={{
              '& .MuiTab-root': {
                minHeight: 60,
                textTransform: 'none',
                fontSize: '1.1rem',
                fontWeight: 500,
                fontFamily: '"Patrick Hand", cursive',
                color: '#2d2d2d',
                border: '2px solid #2d2d2d',
                borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
                margin: '0 8px',
                backgroundColor: tabValue === 0 ? '#fff9c4' : '#ffffff',
                '&.Mui-selected': {
                  color: '#2d2d2d',
                  backgroundColor: '#fff9c4',
                  transform: 'rotate(-1deg)',
                },
                '&:hover': {
                  backgroundColor: '#fff9c4',
                  transform: 'rotate(1deg)',
                },
                transition: 'all 0.1s ease',
              },
              '& .MuiTabs-indicator': {
                display: 'none', // Hide default indicator
              }
            }}
          >
            <Tab 
              icon={
                <Box
                  sx={{
                    width: 32,
                    height: 32,
                    backgroundColor: '#ff4d4d',
                    borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
                    border: '2px solid #2d2d2d',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    mb: 1,
                    boxShadow: '2px 2px 0px 0px #2d2d2d',
                  }}
                >
                  <CloudUpload sx={{ 
                    fontSize: 18, 
                    color: '#ffffff',
                  }} />
                </Box>
              } 
              label="Upload Files ðŸ“" 
              iconPosition="top"
            />
            <Tab 
              icon={
                <Box
                  sx={{
                    width: 32,
                    height: 32,
                    backgroundColor: '#2d5da1',
                    borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
                    border: '2px solid #2d2d2d',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    mb: 1,
                    boxShadow: '2px 2px 0px 0px #2d2d2d',
                  }}
                >
                  <Code sx={{ 
                    fontSize: 18, 
                    color: '#ffffff',
                  }} />
                </Box>
              } 
              label="Paste Code âœï¸" 
              iconPosition="top"
            />
          </Tabs>
        </Box>

        <Box sx={{ p: 4 }}>
          {tabValue === 0 && (
            <Box
              {...getRootProps()}
              sx={{
                border: '3px dashed #2d2d2d',
                borderColor: isDragActive ? '#ff4d4d' : '#2d2d2d',
                borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
                p: 6,
                textAlign: 'center',
                cursor: 'pointer',
                backgroundColor: isDragActive ? 'rgba(255, 77, 77, 0.1)' : 'transparent',
                transition: 'all 0.1s ease',
                position: 'relative',
                transform: isDragActive ? 'rotate(1deg) scale(1.02)' : 'rotate(-0.5deg)',
                '&:hover': {
                  backgroundColor: 'rgba(255, 77, 77, 0.05)',
                  transform: 'rotate(0.5deg) scale(1.01)',
                },
                // Corner decorations
                '&::before': {
                  content: '""',
                  position: 'absolute',
                  top: 16,
                  left: 16,
                  width: 20,
                  height: 20,
                  border: '2px solid #2d2d2d',
                  borderRight: 'none',
                  borderBottom: 'none',
                },
                '&::after': {
                  content: '""',
                  position: 'absolute',
                  bottom: 16,
                  right: 16,
                  width: 20,
                  height: 20,
                  border: '2px solid #2d2d2d',
                  borderLeft: 'none',
                  borderTop: 'none',
                }
              }}
            >
              <input {...getInputProps()} />

              {/* Wobbly Upload Icon */}
              <Box
                sx={{
                  width: 80,
                  height: 80,
                  backgroundColor: isDragActive ? '#ff4d4d' : '#2d5da1',
                  borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
                  border: '3px solid #2d2d2d',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  mx: 'auto',
                  mb: 4,
                  transition: 'all 0.1s ease',
                  boxShadow: '4px 4px 0px 0px #2d2d2d',
                  transform: isDragActive ? 'rotate(5deg)' : 'rotate(-3deg)',
                }}
              >
                <CloudUpload sx={{ 
                  fontSize: 32, 
                  color: '#ffffff',
                }} />
              </Box>

              <Typography 
                variant="h5" 
                sx={{ 
                  mb: 2, 
                  color: '#2d2d2d',
                  fontFamily: '"Kalam", cursive',
                  fontWeight: 700,
                }}
              >
                {isDragActive ? 'Drop them here! ðŸŽ¯' : 'Drop your files here! ðŸ“‚'}
              </Typography>
              <Typography 
                variant="body1" 
                sx={{ 
                  mb: 2,
                  color: '#2d2d2d',
                }}
              >
                Drag and drop Terraform files or click to browse
              </Typography>
              <Typography 
                variant="body2" 
                sx={{ 
                  color: '#2d2d2d',
                  fontFamily: '"Patrick Hand", cursive',
                  opacity: 0.7,
                }}
              >
                We accept: .tf, .hcl files â€¢ Multiple files OK! âœ¨
              </Typography>
            </Box>
          )}

          {tabValue === 1 && (
            <Box>
              <Typography 
                variant="h6" 
                sx={{ 
                  mb: 3, 
                  color: '#2d2d2d',
                  fontFamily: '"Kalam", cursive',
                  fontWeight: 700,
                }}
              >
                Paste Your Terraform Code Here! âœï¸
              </Typography>
              <TextField
                fullWidth
                multiline
                rows={16}
                variant="outlined"
                placeholder={`# Paste your Terraform code here for security scanning!
# Example:

resource "aws_s3_bucket" "my_bucket" {
  bucket = "my-awesome-bucket"

  tags = {
    Name        = "My Bucket"
    Environment = "Production"
  }
}

resource "aws_s3_bucket_versioning" "my_bucket_versioning" {
  bucket = aws_s3_bucket.my_bucket.id
  versioning_configuration {
    status = "Enabled"
  }
}

# We'll check this against 150+ security rules! ðŸ›¡ï¸`}
                value={terraformCode}
                onChange={(e) => setTerraformCode(e.target.value)}
                sx={{
                  mb: 4,
                  '& .MuiInputBase-input': {
                    fontFamily: '"Courier New", monospace',
                    fontSize: '0.9rem',
                    lineHeight: 1.6,
                    color: '#2d2d2d',
                  },
                  '& .MuiOutlinedInput-root': {
                    backgroundColor: '#ffffff',
                    '&:hover': {
                      transform: 'rotate(0.2deg)',
                    },
                    transition: 'all 0.1s ease',
                  },
                }}
              />
              <Button
                variant="contained"
                size="large"
                onClick={handleTextScan}
                disabled={scanning || !terraformCode.trim()}
                sx={{
                  px: 4,
                  py: 2,
                  fontSize: '1.2rem',
                  transform: 'rotate(-1deg)',
                  '&:hover': {
                    transform: 'rotate(1deg) scale(1.05)',
                  }
                }}
              >
                Scan This Code! ðŸš€
              </Button>
            </Box>
          )}
        </Box>
      </Box>

      {/* Hand-Drawn Scanning Progress */}
      {scanning && (
        <Box 
          sx={{ 
            mb: 6, 
            p: 6, 
            backgroundColor: '#fff9c4',
            borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
            border: '3px solid #2d2d2d',
            textAlign: 'center',
            transform: 'rotate(0.5deg)',
            boxShadow: '4px 4px 0px 0px #2d2d2d',
            position: 'relative',
            // Animated decorations
            '&::before': {
              content: '"âš¡"',
              position: 'absolute',
              top: 20,
              left: 30,
              fontSize: '2rem',
              animation: 'bounce 1s infinite',
              '@keyframes bounce': {
                '0%, 100%': { transform: 'translateY(0) rotate(-10deg)' },
                '50%': { transform: 'translateY(-10px) rotate(10deg)' },
              },
            },
            '&::after': {
              content: '"ðŸ”"',
              position: 'absolute',
              top: 20,
              right: 30,
              fontSize: '2rem',
              animation: 'spin 2s linear infinite',
              '@keyframes spin': {
                '0%': { transform: 'rotate(0deg)' },
                '100%': { transform: 'rotate(360deg)' },
              },
            }
          }}
        >
          {/* Wobbly Progress Icon */}
          <Box
            sx={{
              width: 80,
              height: 80,
              backgroundColor: '#ff4d4d',
              borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
              border: '3px solid #2d2d2d',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              mx: 'auto',
              mb: 4,
              boxShadow: '4px 4px 0px 0px #2d2d2d',
              animation: 'wiggle 1s ease-in-out infinite',
              '@keyframes wiggle': {
                '0%, 100%': { transform: 'rotate(-3deg)' },
                '25%': { transform: 'rotate(3deg)' },
                '50%': { transform: 'rotate(-1deg)' },
                '75%': { transform: 'rotate(1deg)' },
              },
            }}
          >
            <Security sx={{ 
              fontSize: 32, 
              color: '#ffffff',
            }} />
          </Box>

          <Typography 
            variant="h4" 
            sx={{ 
              color: '#2d2d2d',
              fontFamily: '"Kalam", cursive',
              fontWeight: 700,
              mb: 2,
            }}
          >
            Scanning Your Code! ðŸ”
          </Typography>
          <Typography 
            variant="body1" 
            sx={{ 
              color: '#2d2d2d',
              mb: 4,
              fontSize: '1.2rem',
            }}
          >
            Checking against 150+ security rules... This won't take long! â°
          </Typography>

          {/* Hand-drawn progress bar */}
          <Box sx={{ position: 'relative', width: '100%', maxWidth: 400, mx: 'auto' }}>
            <Box
              sx={{
                height: 12,
                backgroundColor: '#ffffff',
                border: '2px solid #2d2d2d',
                borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
                position: 'relative',
                overflow: 'hidden',
              }}
            >
              <Box
                sx={{
                  height: '100%',
                  background: 'linear-gradient(90deg, #ff4d4d, #2d5da1, #ff4d4d)',
                  borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
                  animation: 'progress 2s ease-in-out infinite',
                  '@keyframes progress': {
                    '0%': { width: '10%' },
                    '50%': { width: '80%' },
                    '100%': { width: '10%' },
                  },
                }}
              />
            </Box>
          </Box>
        </Box>
      )}

      {/* Hand-Drawn Error Display */}
      {error && (
        <Alert 
          severity="error" 
          sx={{ 
            mb: 4,
            borderRadius: '255px 15px 225px 15px / 15px 225px 15px 255px',
            border: '3px solid #d32f2f',
            backgroundColor: '#ffebee',
            transform: 'rotate(-0.5deg)',
            '& .MuiAlert-message': { 
              fontSize: '1.1rem',
              fontFamily: '"Patrick Hand", cursive',
            }
          }}
          icon={
            <Box
              sx={{
                width: 30,
                height: 30,
                backgroundColor: '#d32f2f',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                border: '2px solid #2d2d2d',
              }}
            >
              <Warning sx={{ 
                fontSize: 18, 
                color: '#ffffff',
              }} />
            </Box>
          }
        >
          Oops! Something went wrong: {error} ðŸ˜…
        </Alert>
      )}

      {/* Hand-Drawn Results Section */}
      {scanResult && (
        <Box>
          {/* Summary Cards */}
          <Box sx={{ mb: 6 }}>
            <Typography 
              variant="h3" 
              sx={{ 
                textAlign: 'center',
                mb: 4,
                color: '#2d2d2d',
                transform: 'rotate(-0.5deg)',
                position: 'relative',
                '&::before': {
                  content: '"ðŸ“Š"',
                  position: 'absolute',
                  left: -50,
                  top: 0,
                  transform: 'rotate(20deg)',
                }
              }}
            >
              Scan Results!
            </Typography>

            <Grid container spacing={4}>
              <Grid item xs={12} md={4}>
