import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Button,
  Chip,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Alert,
  Tabs,
  Tab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Divider,
  Grid
} from '@mui/material';
import {
  CheckCircle,
  Cancel,
  Security,
  Rule,
  Warning,
  Info
} from '@mui/icons-material';
import axios from 'axios';
import { SecurityRule, Severity } from '../types';

interface RulesData {
  active_rules: SecurityRule[];
  candidate_rules: SecurityRule[];
}

const RuleManagement: React.FC = () => {
  const [tabValue, setTabValue] = useState(0);
  const [rulesData, setRulesData] = useState<RulesData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedRule, setSelectedRule] = useState<SecurityRule | null>(null);
  const [dialogOpen, setDialogOpen] = useState(false);

  useEffect(() => {
    fetchRules();
  }, []);

  const fetchRules = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/rules');
      setRulesData(response.data);
      setError(null);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to fetch rules');
    } finally {
      setLoading(false);
    }
  };

  const handleApproveRule = async (ruleId: string) => {
    try {
      await axios.post(`/api/rules/${ruleId}/approve`);
      await fetchRules(); // Refresh the rules list
      setDialogOpen(false);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to approve rule');
    }
  };

  const handleRejectRule = async (ruleId: string) => {
    try {
      await axios.post(`/api/rules/${ruleId}/reject`);
      await fetchRules(); // Refresh the rules list
      setDialogOpen(false);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to reject rule');
    }
  };

  const getSeverityIcon = (severity: Severity) => {
    switch (severity) {
      case Severity.CRITICAL:
        return <Warning color="error" />;
      case Severity.HIGH:
        return <Warning color="warning" />;
      case Severity.MEDIUM:
        return <Info color="info" />;
      case Severity.LOW:
        return <Info color="action" />;
      default:
        return <Info />;
    }
  };

  const getSeverityColor = (severity: Severity) => {
    switch (severity) {
      case Severity.CRITICAL:
        return 'error';
      case Severity.HIGH:
        return 'warning';
      case Severity.MEDIUM:
        return 'info';
      case Severity.LOW:
        return 'default';
      default:
        return 'default';
    }
  };

  const openRuleDialog = (rule: SecurityRule) => {
    setSelectedRule(rule);
    setDialogOpen(true);
  };

  const RuleList: React.FC<{ rules: SecurityRule[]; showActions?: boolean }> = ({ 
    rules, 
    showActions = false 
  }) => (
    <List>
      {rules.map((rule) => (
        <ListItem key={rule.id} divider>
          <ListItemText
            primary={
              <Box display="flex" alignItems="center" gap={1}>
                {getSeverityIcon(rule.severity)}
                <Typography variant="subtitle1">
                  {rule.name}
                </Typography>
                <Chip
                  label={rule.severity}
                  color={getSeverityColor(rule.severity)}
                  size="small"
                />
                <Chip
                  label={rule.source}
                  variant="outlined"
                  size="small"
                />
              </Box>
            }
            secondary={
              <Box>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  {rule.description}
                </Typography>
                <Typography variant="caption" color="text.secondary">
                  Created: {new Date(rule.created_at).toLocaleDateString()}
                </Typography>
              </Box>
            }
          />
          <Box sx={{ display: 'flex', gap: 1, alignItems: 'center' }}>
            <Button
              size="small"
              onClick={() => openRuleDialog(rule)}
            >
              View Details
            </Button>
            {showActions && (
              <>
                <Button
                  size="small"
                  color="success"
                  startIcon={<CheckCircle />}
                  onClick={() => handleApproveRule(rule.id)}
                >
                  Approve
                </Button>
                <Button
                  size="small"
                  color="error"
                  startIcon={<Cancel />}
                  onClick={() => handleRejectRule(rule.id)}
                >
                  Reject
                </Button>
              </>
            )}
          </Box>
        </ListItem>
      ))}
    </List>
  );

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" p={4}>
        <Typography>Loading rules...</Typography>
      </Box>
    );
  }

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Security Rule Management
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      <Card>
        <CardContent>
          <Tabs value={tabValue} onChange={(_, newValue) => setTabValue(newValue)}>
            <Tab
              icon={<Security />}
              label={`Active Rules (${rulesData?.active_rules.length || 0})`}
            />
            <Tab
              icon={<Rule />}
              label={`Candidate Rules (${rulesData?.candidate_rules.length || 0})`}
            />
          </Tabs>

          <Box sx={{ mt: 2 }}>
            {tabValue === 0 && rulesData && (
              <Box>
                {rulesData.active_rules.length === 0 ? (
                  <Typography color="text.secondary" textAlign="center" py={4}>
                    No active rules found
                  </Typography>
                ) : (
                  <RuleList rules={rulesData.active_rules} />
                )}
              </Box>
            )}

            {tabValue === 1 && rulesData && (
              <Box>
                {rulesData.candidate_rules.length === 0 ? (
                  <Typography color="text.secondary" textAlign="center" py={4}>
                    No candidate rules found
                  </Typography>
                ) : (
                  <RuleList rules={rulesData.candidate_rules} showActions />
                )}
              </Box>
            )}
          </Box>
        </CardContent>
      </Card>

      {/* Rule Details Dialog */}
      <Dialog
        open={dialogOpen}
        onClose={() => setDialogOpen(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          <Box display="flex" alignItems="center" gap={1}>
            {selectedRule && getSeverityIcon(selectedRule.severity)}
            Rule Details
          </Box>
        </DialogTitle>
        <DialogContent>
          {selectedRule && (
            <Box>
              <Grid container spacing={2} sx={{ mb: 2 }}>
                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Name
                  </Typography>
                  <Typography variant="body1">
                    {selectedRule.name}
                  </Typography>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Severity
                  </Typography>
                  <Chip
                    label={selectedRule.severity}
                    color={getSeverityColor(selectedRule.severity)}
                    size="small"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Source
                  </Typography>
                  <Chip
                    label={selectedRule.source}
                    variant="outlined"
                    size="small"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Status
                  </Typography>
                  <Chip
                    label={selectedRule.status}
                    color={selectedRule.status === 'ACTIVE' ? 'success' : 'default'}
                    size="small"
                  />
                </Grid>
              </Grid>

              <Divider sx={{ my: 2 }} />

              <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                Description
              </Typography>
              <Typography variant="body1" paragraph>
                {selectedRule.description}
              </Typography>

              <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                Pattern
              </Typography>
              <Typography
                variant="body2"
                sx={{
                  fontFamily: 'monospace',
                  bgcolor: 'grey.100',
                  p: 1,
                  borderRadius: 1,
                  mb: 2
                }}
              >
                {selectedRule.pattern}
              </Typography>

              <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                Remediation
              </Typography>
              <Typography variant="body1">
                {selectedRule.remediation}
              </Typography>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          {selectedRule?.status === 'CANDIDATE' && (
            <>
              <Button
                color="success"
                startIcon={<CheckCircle />}
                onClick={() => selectedRule && handleApproveRule(selectedRule.id)}
              >
                Approve
              </Button>
              <Button
                color="error"
                startIcon={<Cancel />}
                onClick={() => selectedRule && handleRejectRule(selectedRule.id)}
              >
                Reject
              </Button>
            </>
          )}
          <Button onClick={() => setDialogOpen(false)}>
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default RuleManagement;
