import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Button,
  Chip,
  List,
  ListItem,
  ListItemText,

  Alert,
  Tabs,
  Tab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Divider,
  Grid
} from '@mui/material';
import {
  CheckCircle,
  Cancel,
  Security,
  Rule,
  Warning,
  Info
} from '@mui/icons-material';
import apiClient from '../config/api';
import { SecurityRule, Severity } from '../types';

interface RulesData {
  active_rules: SecurityRule[];
  candidate_rules: SecurityRule[];
}

const RuleManagement: React.FC = () => {
  const [tabValue, setTabValue] = useState(0);
  const [rulesData, setRulesData] = useState<RulesData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedRule, setSelectedRule] = useState<SecurityRule | null>(null);
  const [dialogOpen, setDialogOpen] = useState(false);

  useEffect(() => {
    fetchRules();
  }, []);

  const fetchRules = async () => {
    try {
      setLoading(true);
      const response = await apiClient.get('/api/rules');
      setRulesData(response.data);
      setError(null);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to fetch rules');
    } finally {
      setLoading(false);
    }
  };

  const handleApproveRule = async (ruleId: string) => {
    try {
      await apiClient.post(`/api/rules/${ruleId}/approve`);
      await fetchRules(); // Refresh the rules list
      setDialogOpen(false);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to approve rule');
    }
  };

  const handleRejectRule = async (ruleId: string) => {
    try {
      await apiClient.post(`/api/rules/${ruleId}/reject`);
      await fetchRules(); // Refresh the rules list
      setDialogOpen(false);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to reject rule');
    }
  };

  const getSeverityIcon = (severity: Severity) => {
    switch (severity) {
      case Severity.CRITICAL:
        return <Warning color="error" />;
      case Severity.HIGH:
        return <Warning color="warning" />;
      case Severity.MEDIUM:
        return <Info color="info" />;
      case Severity.LOW:
        return <Info color="action" />;
      default:
        return <Info />;
    }
  };

  const getSeverityColor = (severity: Severity) => {
    switch (severity) {
      case Severity.CRITICAL:
        return 'error';
      case Severity.HIGH:
        return 'warning';
      case Severity.MEDIUM:
        return 'info';
      case Severity.LOW:
        return 'default';
      default:
        return 'default';
    }
  };

  const openRuleDialog = (rule: SecurityRule) => {
    setSelectedRule(rule);
    setDialogOpen(true);
  };

  const RuleList: React.FC<{ rules: SecurityRule[]; showActions?: boolean }> = ({ 
    rules, 
    showActions = false 
  }) => (
    <List>
      {rules.map((rule) => (
        <ListItem key={rule.id} divider>
          <ListItemText
            primary={
              <Box display="flex" alignItems="center" gap={1}>
                {getSeverityIcon(rule.severity)}
                <Typography variant="subtitle1">
                  {rule.name}
                </Typography>
                <Chip
                  label={rule.severity}
                  color={getSeverityColor(rule.severity)}
                  size="small"
                />
                <Chip
                  label={rule.source}
                  variant="outlined"
                  size="small"
                />
              </Box>
            }
            secondary={
              <Box>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  {rule.description}
                </Typography>
                <Typography variant="caption" color="text.secondary">
                  Created: {new Date(rule.created_at).toLocaleDateString()}
                </Typography>
              </Box>
            }
          />
          <Box sx={{ display: 'flex', gap: 1, alignItems: 'center' }}>
            <Button
              size="small"
              onClick={() => openRuleDialog(rule)}
            >
              View Details
            </Button>
            {showActions && (
              <>
                <Button
                  size="small"
                  color="success"
                  startIcon={<CheckCircle />}
                  onClick={() => handleApproveRule(rule.id)}
                >
                  Approve
                </Button>
                <Button
                  size="small"
                  color="error"
                  startIcon={<Cancel />}
                  onClick={() => handleRejectRule(rule.id)}
                >
                  Reject
                </Button>
              </>
            )}
          </Box>
        </ListItem>
      ))}
    </List>
  );

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" p={4}>
        <Typography>Loading rules...</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ minHeight: '100vh', p: 4 }}>
      {/* Art Deco Header Section */}
      <Box sx={{ textAlign: 'center', mb: 8, position: 'relative' }}>
        {/* Decorative Top Line */}
        <Box sx={{ 
          position: 'absolute',
          top: 0,
          left: '50%',
          transform: 'translateX(-50%)',
          width: 300,
          height: 4,
          background: 'linear-gradient(90deg, transparent, #D4AF37, transparent)',
        }} />

        {/* Diamond Icon */}
        <Box
          sx={{
            width: 100,
            height: 100,
            backgroundColor: '#D4AF37',
            transform: 'rotate(45deg)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            mx: 'auto',
            mb: 4,
            position: 'relative',
            boxShadow: '0 0 30px rgba(212, 175, 55, 0.3)',
            '&::before': {
              content: '""',
              position: 'absolute',
              top: 6,
              left: 6,
              right: 6,
              bottom: 6,
              border: '2px solid #0A0A0A',
            }
          }}
        >
          <Rule 
            sx={{ 
              fontSize: 40, 
              color: '#0A0A0A',
              transform: 'rotate(-45deg)',
            }} 
          />
        </Box>

        <Typography 
          variant="h2" 
          sx={{ 
            color: '#D4AF37',
            mb: 2,
            textShadow: '0 0 20px rgba(212, 175, 55, 0.3)',
          }}
        >
          Rule Management
        </Typography>
        <Typography 
          variant="h5" 
          sx={{ 
            color: '#888888',
            textTransform: 'uppercase',
            letterSpacing: '0.2em',
            mb: 4,
          }}
        >
          Security Rule Administration
        </Typography>
      </Box>

      {/* Art Deco Error Display */}
      {error && (
        <Box 
          sx={{ 
            mb: 6,
            p: 4,
            border: '2px solid #d32f2f',
            backgroundColor: 'rgba(211, 47, 47, 0.05)',
            position: 'relative',
          }}
        >
          <Box
            sx={{
              position: 'absolute',
              top: 16,
              left: 16,
              width: 24,
              height: 24,
              borderTop: '2px solid #d32f2f',
              borderLeft: '2px solid #d32f2f',
            }}
          />
          <Typography variant="body1" sx={{ color: '#F2F0E4' }}>
            {error}
          </Typography>
        </Box>
      )}

      {/* Art Deco Main Card */}
      <Box 
        sx={{ 
          border: '2px solid #D4AF37',
          position: 'relative',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: -2,
            left: 40,
            right: 40,
            height: 2,
            backgroundColor: '#0A0A0A',
          },
          '&::after': {
            content: '""',
            position: 'absolute',
            bottom: -2,
            left: 40,
            right: 40,
            height: 2,
            backgroundColor: '#0A0A0A',
          }
        }}
      >
        {/* Art Deco Tabs */}
        <Box sx={{ borderBottom: '1px solid rgba(212, 175, 55, 0.3)' }}>
          <Tabs 
            value={tabValue} 
            onChange={(_, newValue) => setTabValue(newValue)}
            sx={{
              '& .MuiTab-root': {
                minHeight: 80,
                textTransform: 'uppercase',
                fontSize: '1rem',
                fontWeight: 500,
                letterSpacing: '0.1em',
                color: '#888888',
                position: 'relative',
                '&.Mui-selected': {
                  color: '#D4AF37',
                },
              },
              '& .MuiTabs-indicator': {
                backgroundColor: '#D4AF37',
                height: 3,
              }
            }}
          >
            <Tab
              icon={
                <Box
                  sx={{
                    width: 32,
                    height: 32,
                    transform: 'rotate(45deg)',
                    backgroundColor: tabValue === 0 ? '#D4AF37' : 'transparent',
                    border: `1px solid ${tabValue === 0 ? '#D4AF37' : 'rgba(212, 175, 55, 0.5)'}`,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    transition: 'all 0.3s ease',
                  }}
                >
                  <Security sx={{ 
                    fontSize: 18, 
                    transform: 'rotate(-45deg)',
                    color: tabValue === 0 ? '#0A0A0A' : 'currentColor',
                  }} />
                </Box>
              }
              label={`Active Rules (${rulesData?.active_rules.length || 0})`}
              iconPosition="top"
            />
            <Tab
              icon={
                <Box
                  sx={{
                    width: 32,
                    height: 32,
                    transform: 'rotate(45deg)',
                    backgroundColor: tabValue === 1 ? '#D4AF37' : 'transparent',
                    border: `1px solid ${tabValue === 1 ? '#D4AF37' : 'rgba(212, 175, 55, 0.5)'}`,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    transition: 'all 0.3s ease',
                  }}
                >
                  <Rule sx={{ 
                    fontSize: 18, 
                    transform: 'rotate(-45deg)',
                    color: tabValue === 1 ? '#0A0A0A' : 'currentColor',
                  }} />
                </Box>
              }
              label={`Candidate Rules (${rulesData?.candidate_rules.length || 0})`}
              iconPosition="top"
            />
          </Tabs>
        </Box>

        <Box sx={{ p: 6 }}>
          {tabValue === 0 && rulesData && (
            <Box>
              {rulesData.active_rules.length === 0 ? (
                <Box sx={{ textAlign: 'center', py: 8 }}>
                  <Box
                    sx={{
                      width: 80,
                      height: 80,
                      transform: 'rotate(45deg)',
                      backgroundColor: 'rgba(212, 175, 55, 0.1)',
                      border: '2px solid rgba(212, 175, 55, 0.3)',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      mx: 'auto',
                      mb: 3,
                    }}
                  >
                    <Security sx={{ 
                      fontSize: 32, 
                      color: '#888888',
                      transform: 'rotate(-45deg)',
                    }} />
                  </Box>
                  <Typography 
                    variant="h6" 
                    sx={{ 
                      color: '#888888',
                      textTransform: 'uppercase',
                      letterSpacing: '0.1em',
                    }}
                  >
                    No Active Rules Found
                  </Typography>
                </Box>
              ) : (
                <RuleList rules={rulesData.active_rules} />
              )}
            </Box>
          )}

          {tabValue === 1 && rulesData && (
            <Box>
              {rulesData.candidate_rules.length === 0 ? (
                <Box sx={{ textAlign: 'center', py: 8 }}>
                  <Box
                    sx={{
                      width: 80,
                      height: 80,
                      transform: 'rotate(45deg)',
                      backgroundColor: 'rgba(212, 175, 55, 0.1)',
                      border: '2px solid rgba(212, 175, 55, 0.3)',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      mx: 'auto',
                      mb: 3,
                    }}
                  >
                    <Rule sx={{ 
                      fontSize: 32, 
                      color: '#888888',
                      transform: 'rotate(-45deg)',
                    }} />
                  </Box>
                  <Typography 
                    variant="h6" 
                    sx={{ 
                      color: '#888888',
                      textTransform: 'uppercase',
                      letterSpacing: '0.1em',
                    }}
                  >
                    No Candidate Rules Found
                  </Typography>
                </Box>
              ) : (
                <RuleList rules={rulesData.candidate_rules} showActions />
              )}
            </Box>
          )}
        </Box>
      </Box>

      {/* Rule Details Dialog */}
      <Dialog
        open={dialogOpen}
        onClose={() => setDialogOpen(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          <Box display="flex" alignItems="center" gap={1}>
            {selectedRule && getSeverityIcon(selectedRule.severity)}
            Rule Details
          </Box>
        </DialogTitle>
        <DialogContent>
          {selectedRule && (
            <Box>
              <Grid container spacing={2} sx={{ mb: 2 }}>
                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Name
                  </Typography>
                  <Typography variant="body1">
                    {selectedRule.name}
                  </Typography>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Severity
                  </Typography>
                  <Chip
                    label={selectedRule.severity}
                    color={getSeverityColor(selectedRule.severity)}
                    size="small"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Source
                  </Typography>
                  <Chip
                    label={selectedRule.source}
                    variant="outlined"
                    size="small"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Status
                  </Typography>
                  <Chip
                    label={selectedRule.status}
                    color={selectedRule.status === 'ACTIVE' ? 'success' : 'default'}
                    size="small"
                  />
                </Grid>
              </Grid>

              <Divider sx={{ my: 2 }} />

              <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                Description
              </Typography>
              <Typography variant="body1" paragraph>
                {selectedRule.description}
              </Typography>

              <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                Pattern
              </Typography>
              <Typography
                variant="body2"
                sx={{
                  fontFamily: 'monospace',
                  bgcolor: 'grey.100',
                  p: 1,
                  borderRadius: 1,
                  mb: 2
                }}
              >
                {selectedRule.pattern}
              </Typography>

              <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                Remediation
              </Typography>
              <Typography variant="body1">
                {selectedRule.remediation}
              </Typography>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          {selectedRule?.status === 'CANDIDATE' && (
            <>
              <Button
                color="success"
                startIcon={<CheckCircle />}
                onClick={() => selectedRule && handleApproveRule(selectedRule.id)}
              >
                Approve
              </Button>
              <Button
                color="error"
                startIcon={<Cancel />}
                onClick={() => selectedRule && handleRejectRule(selectedRule.id)}
              >
                Reject
              </Button>
            </>
          )}
          <Button onClick={() => setDialogOpen(false)}>
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default RuleManagement;
